SET NAMES utf8mb4;
SET FOREIGN_KEY_CHECKS = 0;

-- ----------------------------
-- Table structure for news
-- ----------------------------
DROP TABLE IF EXISTS `news`;
CREATE TABLE `news`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '新闻id',
  `topic` varchar(5) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '主题名',
  `title` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '标题',
  `author` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '作者',
  `summary` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '摘要',
  `content` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '内容',
  `creattime` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '创建时间',
  `creatby` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '创建者',
  `modifytime` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '最近一次修改时间',
  `modifyby` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '最近一次修改时间',
  `frequency` int(255) NULL DEFAULT NULL COMMENT '查看次数',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `主题`(`topic`) USING BTREE,
  CONSTRAINT `主题` FOREIGN KEY (`topic`) REFERENCES `topic` (`topicname`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 39 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

-- ----------------------------
-- Records of news
-- ----------------------------
INSERT INTO `news` VALUES (1, '国际新闻', '拆雕像改名字，美国的一场历史大清算？', '张梦旭 陈圣源 青木 ', '当地时间9日，美国纽约市长白思豪表示，该市将在每个行政区的“关键”地段选择一条街道，命名为“黑人的命也是命”街。在反警察暴力执法和种族歧视抗议活动推动下，美国社会正经历一场洗礼，除了这样的命名之举，更突出的是针对150多年前内战战败一方公共纪念物的“清算”——拆除雕像，更改名字。', '“遍布全美的邦联纪念物正在倒下”\r\n\r\n“遍布全美的邦联纪念物正在倒下。”美国《史密森尼》杂志网站9日称，在对警察暴力执法和系统性种族主义的抗议声中，美国公众及政府官员正“清算”与奴隶主、邦联及其他争议性政治人物有关的公共纪念物，多个州的政府已经承诺或开始这么做。\r\n\r\n8日，在美国肯塔基州最大城市路易斯维尔，工作人员拆除了内战时期任南方邦联军将领的约翰·卡斯尔曼的雕像。市政府计划将雕像安放在他被安葬的洞穴山公墓，并表示正与公墓方进行协调。卡斯尔曼出生于1841年，20岁时加入邦联军将领约翰·摩根旗下的肯塔基州第二骑兵团。\r\n\r\n路易斯维尔市长、民主党人费舍尔多年来一直希望拆除这座雕像。一名反种族歧视活动人士表示，她为此争取了3年，因为“这是奴隶制的遗迹”。也有人感到失望。一名当地民众说，这座雕像有100多年历史，是社区的象征，“大多数人不愿去了解有关卡斯尔曼的完整历史。他为这座城市做了很多。他确实曾加入南方阵营，但整体看，我觉得它值得留在这儿”。\r\n\r\n路易斯维尔发生的事情，是全美一场普遍性行动的一部分。几天前，在弗吉尼亚州首府里士满，抗议者推倒内战期间南方邦联军将军威廉斯·威克姆的雕像。弗州州长此前还宣布，将拆除位于里士满历史遗迹大道上的邦联军将军罗伯特·李的纪念碑。在肯塔基州，州长安迪·戴维斯已要求将南方邦联总统杰弗逊·戴维斯的雕像从州议会大厦移除。\r\n\r\n在亚拉巴马州莫比尔市，该市官员已将南方邦联军海军上将拉斐尔·塞姆斯的雕像从市中心移除。在路易斯安那州，尼科尔斯州立大学正准备更改两座学院大楼的名字，因为它们都以邦联军将军的姓名命名。在黑人人口占38%的密西西比州，该州是全美唯一将邦联战旗融入州旗的州，如今承受着越来越大换旗的压力。但该州共和党籍州长反对撤换，8日他在媒体逼问下表示，应由人民决定，而非议员们。\r\n\r\n美国军方也有动作。据美国《政治》杂志8日报道，美国陆军有10个基地和设施以南方邦联军领导人名字命名，陆军部长瑞安·麦卡锡已表示对重新命名这些基地持开放态度。国防部长埃斯珀对此也表示支持。\r\n\r\n有分析称，在重新命名这些陆军军事基地问题上，军队面临着一场艰苦的战斗。要求重新命名的呼声存在多年，美国陆军此前坚持认为，以“具有军事重要性的历史人物（包括内战的双方将领）”命名设施和街道是陆军“传统”，旨在纪念和颂扬美军士兵，而更名将“颠覆传统”。\r\n\r\n这些争议性基地基本都在南方各州，比如北卡罗来纳州的布拉格堡，邦联军将军布拉克斯顿·布拉格是一个奴隶主，他被认为是内战期间最无能的将军之一。此外还有佐治亚州的本宁堡和戈登堡、弗吉尼亚州的皮克特堡、得克萨斯州的胡德堡等。\r\n\r\n比陆军动作还快的是美国海军陆战队——5日率先“封杀”邦联旗帜，禁止陆战队员在任何场合以任何形式展示邦联旗。9日，美国海军也宣布“封杀”邦联旗，禁止在海军设施、舰船、战机及潜艇上的所有公共空间和工作区域悬挂邦联旗帜。 \r\n\r\n为支持非洲裔的抗争，一些城市还有其他动作，比如首都华盛顿特区市长5日下令在通往白宫的街道上涂上“黑人的命也是命”标语，并将白宫北部拉斐特公园前面的广场命名为“黑人的命也是命”广场。9日，纽约市长白思豪宣布将对每个行政区的道路进行粉刷并重新命名。\r\n\r\n拆还是不拆，这是一个问题\r\n\r\n针对种族歧视象征特别是邦联遗迹的拆除风潮，过去5年出现过两次。一次是2015年，由南卡罗来纳州查尔斯顿教堂枪击案引发，当时一名白人开枪打死9人。另一次是2017年，弗吉尼亚州夏洛茨维尔发生种族暴力事件。\r\n\r\n2017年的那次事件中，美国各地包括旗帜和纪念物在内的邦联标志物令抗议民众怒火中烧，但在几十个相关匾额、雕塑等遭质疑或被拆除后，再没有任何有关处理此类地标的统一计划。近年来，一些纪念物被拍卖、移走、储存，但其他仍原封不动。据美国民权团体“南方贫困法律中心”2019年统计，自查尔斯顿事件后，有114个邦联标志物被移除，但仍有1747个继续存在。\r\n\r\n“那些雕像之类的标识是内战的遗产，当年很多原南方的将军、高官没有被追责，甚至继续做了高官。近些年，由于‘政治正确’，一些有色族裔自发来做这些事，引发美国社会震动，政府也没太多干预。”中国社科院美国研究所研究员袁征对《环球时报》记者说，但这次，一名黑人死在人们眼皮底下，而且今年是大选年，无论哪个党都有意迎合这批人，不想在这个问题上得罪他们。\r\n\r\n对支持拆除雕像的人来说，这些纪念物一直是白人至上主义者的“图腾”。哈佛大学非洲裔美国人研究系讲师琳达·查弗斯在《波士顿环球报》上撰文说，许多白人不了解种族主义，将种族主义视为过去的遗迹。查弗斯称，他们不知道黑人历史上的最低点并不是在奴隶制时期，而是在20世纪之交，白人种族主义者对黑人获得的独立地位，对黑人在国会中的存在感到不满，对黑人的种族主义经历了致命反弹。\r\n\r\n这的确是事实。美国内战结束约30年后，即从19世纪末到20世纪20年代，随着南方各州陆续执行种族隔离的《吉姆·克劳法》，邦联纪念物大量出现。30年后，民权运动兴起带来种族主义者反弹，邦联纪念物再次涌现。\r\n\r\n不过，关于如何处理这些历史遗迹，一直争议很大。有人认为，这些雕像早就该拆除，政府此前很无知，各地应成立委员会，深入了解种族主义的历史遗产。反对者则认为，这是破坏行为，雕像是对历史的记录，拆雕像并不能解决种族主义问题。“我们拥有自己的历史很重要。我们可以拆掉这个国家所有的邦联纪念物，但如果不能从中吸取教训，种族主义不会被终结。”纽约城市大学艺术历史学家哈丽雅特·森尼说。\r\n\r\n与美国几乎所有社会问题一样，“黑人的命也是命”这一运动不乏反对者，除了白人至上主义者，有温和反对者提出“所有人的命都是命”，但自由派人士认为，这么说表面正确，其实是对种族问题的忽视或刻意回避。在目前的大环境下，连一些共和党保守人士也站出来支持“黑人的命也是命”运动。\r\n\r\n拆除邦联纪念物运动形成风潮，但也面临障碍。美国南方多州有保护这些纪念物的法律，州级立法机构也多被共和党所掌控。美国VOX网站称，在肯塔基和路易斯安那等前邦联州，一些官员支持拆除行动。但并非所有人都如此，多年前开展此类讨论时，许多政客就反对，这次他们又在做同样的事情。  \r\n\r\n在美国国会大厦国家雕像厅，罗伯特·李、邦联总统与副总统等人的雕塑仍赫然在列。国会大厦内还有奴隶制坚定支持者、美国前副总统约翰·卡尔霍恩，以及北卡罗来纳州前州长、白人至上主义领导人查尔斯·艾考克的雕塑。几年前，一些黑人议员曾要求替换这些雕塑，但此类努力未能成功。\r\n\r\n背后是美国在走向“极化”\r\n\r\n“靠拆除邦联纪念物不可能清除种族主义。”袁征对《环球时报》记者说，在白人主导的美国社会，对有色人种尤其黑人的歧视不是法律造成的，而是内心深处白人至上的意识所致。长远看，美国的种族、移民问题会更突出。到本世纪中叶，白人人口占比可能会降到50%以下，虽然绝对数量依然最多，但他们会越来越焦虑。\r\n\r\n受美国影响，欧洲近期的反种族歧视抗议活动中，多个历史人物被卷入。在英国城市布里斯托，17世纪奴隶贩子爱德华·科尔斯顿的铜像被推倒。伦敦、曼彻斯特都表示将重新审查市内的地标、雕塑等。在比利时，多地发生破坏国王利奥波德二世雕像的事件。\r\n\r\n德国新闻电视台9日称，西方此前曾对东欧及其他地区推倒历史雕像，比如与苏联有关的雕像给予欢迎，但最近美英等国拆除争议人物雕像的运动却引起舆论分歧，变成一场“政治之争”。德国历史学家尤尔根·齐默勒表示，包括德国在内的许多国家都有纪念奴隶贩子或在殖民主义中起重要作用的人的纪念物，美国现在的拆雕像事件，给这些国家带来难题。他认为，一边倒地推翻雕像，是将种族问题简单化，也是对文化历史的“暴力”，应让争议雕像先经历社会讨论，然后决定去留，大多数可以作为历史的见证。\r\n\r\n在当下的美国，有关拆除邦联纪念物的不同看法互不相让，有党派之争，有意识形态差异。复旦大学国际问题研究院院长吴心伯告诉《环球时报》记者，从官方动作看，拆不拆雕像主要看州长或市长的政治倾向。在当前抗议的大背景下，南方一些地方为缓和矛盾，也可能是为回应民间的声音，所以做出支持拆除的决定。但总体上，这并不代表美国整个政治潮流发生了变化。\r\n\r\n“走向极化的国家。”德国《焦点》周刊称，近几十年来最大的疫情、上世纪60年代以来最大的抗议活动及近几十年来最严重的经济危机，让美国陷入混乱，并走向极端化。推倒雕像是这种极端化的一个象征，美国人在发泄愤怒，美国社会出了新的大问题。美国《外交》杂志也称，美国正同时经历疫情、经济衰退、抗议“三场剧变”，美国驻欧洲等地的使馆周围爆发反对种族主义和警察暴行的示威，多年来，对美国榜样的信心日趋衰落，而这是美国国内长期政治分裂和机能失调的结果。\r\n\r\n从1619年第一艘运送奴隶的船只抵达弗吉尼亚海岸算起，美国非洲裔争取权利的斗争已经持续4个多世纪，之所以至今还在持续，用美国霍华德大学法学教授卡尔顿·沃特豪斯的话说，问题在于结构性种族主义从未成为美国总体政策论述的一部分，有关民权的法律制定被用于针对不合适的个人行为，并没有解决非裔群体的所有问题。\r\n\r\n在吴心伯看来，美国对未来发展方向的看法越来越分裂。奥巴马代表了一个方向，就是左一点、进步主义，而特朗普执政反映出美国要回到过去，即二战前那种白人主导、种族主义倾向，对外搞孤立主义和单边主义。“这两派都有强大的社会基础，所以美国在国家发展方向上越来越分裂，越来越困惑，短期内不会有答案。”', '2020-06-11 04:53:43', '王安明', '2020-06-20 06:26:02', 'wam', 11);
INSERT INTO `news` VALUES (2, '国际新闻', '日本想主导G7发表涉港声明 外交部：已向日方表明严重关切', '孙秀萍 陈洋 李翔宇', '日本首相安倍晋三10日在众议院接受质询时表示，为向国际社会传递有关香港问题立场的必要信息，日本计划在下一次七国集团（G7）外长会上牵头起草一份有关涉港国安立法和“一国两制”的联合声明。中国外交部发言人华春莹10日在例行记者会上就此回应称，注意到有关报道，已向日方表明严重关切。', '据日本时事通讯社10日报道，安倍当天称，“对于在香港发生的各种各样的事情，日本也在表达担忧。”“日本意识到G7有引导国际舆论的使命，以‘一国两制’为前提，日本希望就香港问题在G7框架内牵头起草一份声明。”此前一天，日本外相茂木敏充在记者会上表达了类似观点。\r\n\r\n日本共同社近日称，日本政府拒绝加入美英等国的涉港联合声明。而几天后政府的表态却出现逆转，引发广泛关注。日本《朝日新闻》分析认为，越来越多的声音要求对华采取强硬政策，是安倍政权态度转变的原因。不过也有分析人士称，疫情之下，安倍政权支持率持续下跌。在无法迅速找到良策振兴经济的情况下，只好寄望于通过外交举措稳住支持率。\r\n\r\n一名不愿透露姓名的日本问题研究学者10日对《环球时报》记者表示，在中美之间，日本更多时候还是会选择站在靠近美国的一边。不过，从“拒绝参与”到“在G7内发挥主导”，反映出“后疫情时代”安倍政权对中国的复杂心态。新冠肺炎疫情对日本经济冲击巨大，安倍政权需要强化与中国的经贸往来，所以并不希望得罪中国，但由于日本国内保守势力的叫嚣，以及来自美国的压力，又使得政府不得不做出“强硬姿态”，否则就容易被扣上“亲华”“对华软弱”的标签。\r\n\r\n华春莹10日重申，中国全国人大就建立健全香港特别行政区维护国家安全的法律制度和执行机制做出决定，完全属于中国内政，任何外国无权干涉。有关国家应遵守国际法和国际关系基本准则。', '2020-06-11 05:31:33', 'wam', '2020-06-20 08:55:48', 'wam', 6);
INSERT INTO `news` VALUES (3, '国际新闻', '朝鲜宣布切断朝韩通讯联络线后 韩统一部控告“脱北者”团体', '济冬', '在朝鲜宣布切断朝韩之间一切通讯联络线后，韩国统一部10日对两家违法散发反朝传单的“脱北者”团体提起诉讼，并决定取消其成立许可。', '据韩国《中央日报》报道，韩国统一部10日表示，上述两家团体散发反朝传单和物资的行为违反《韩朝交流合作法》，该行为与韩朝领导人达成的协议产生正面冲突，造成韩朝关系紧张，威胁边境地区居民的生命财产安全，致使社会公共利益受到侵害。\r\n\r\n“脱北者”团体散发反朝传单一事引发朝鲜的强烈不满。为表示抗议，朝中社9日发表公报说，将从当天中午12时开始切断朝韩之间一切通讯联络线。美国国务院9日表示，对朝方此举感到失望，敦促朝方回到外交与合作轨道。\r\n\r\n韩国《韩民族日报》认为，统一部的举动可能与朝鲜的最新决定有关，是韩国政府最大限度采取措施管控局面的行为。韩国《京乡新闻》称，统一部对“脱北者”团体采取“超强硬措施”，是因为政府不能再继续放任韩朝关系因反朝传单问题陷入中断局面。不过据韩国《每日经济》报道，政府对朝的“低姿态”将引起争议。《韩民族日报》称，政府对“脱北者”团体进行惩罚的依据是相关法律中“对朝运出物资前需获得统一部长官批准”这一规定，但此前韩方并未规定散发反朝传单需要获得批准。韩国《朝鲜日报》对政府的行为表示不满，“是不是每次朝鲜发火，韩国的法律解释就要改变呢？”\r\n\r\n对于批评政府在对朝问题上过度放低姿态的声音，韩国统一部发言人吕尚基10日表示，在对朝事务上感情用事不可取。另据韩联社报道，韩国国防部长官郑景斗10日称，定于上半年的韩美联合空军战备训练和韩美导弹防御系统整合及联动演习已正常实施。韩美两军近日在假设朝鲜发射导弹的情况进行交换情报联合拦截的演习。韩国SBS电视台称，有观点认为，在目前的局面下，韩国公布韩美军演是为强调韩美联合防卫态势。', '2020-06-11 05:32:31', 'wam', '无', '无', 4);
INSERT INTO `news` VALUES (4, '国内新闻', '年内30多地发购房补贴政策 6地区现百万级红包', '中新经纬', '中新经纬客户端6月11日电 (冯方)近日，南京江北新区印发通知，提出最高300万元购房补贴的政策。此前，郑州、南宁等地区分别发布或调整购房补贴政策。据统计，年内至少已有30多个地区发布或调整购房补贴政策，其中，有6个地区最高补贴金额超过百万。', '高层次人才6地区购房可享百万级补贴\r\n\r\n根据各地政府部门文件以及公开报道，今年以来，多个地区发布或调整对引进人才、医护人员、农民工等群体给予购房补贴的政策。从金额来看，有6个地区补贴金额达到百万级别，分别为杭州、苏州工业园区、南京江北新区、台州、南宁和济南。\r\n\r\n其中，杭州最高补贴额度居首，达800万元。今年2月，杭州推出“战疫引才、杭向未来”八大举措，提高高层次人才购房补贴，对A类顶尖人才按“一人一议”最高给予800万元购房补贴，B、C、D类人才分别给予200万元、150万元、100万元购房补贴。\r\n\r\n中国(江苏)自由贸易试验区的南京片区和苏州片区也有相关政策推出。6月5日，南京江北新区(南京片区所属辖区)印发通知，实施区块链顶尖人才集聚计划，经认定的可享受最高300万元购房补贴。4月底，苏州工业园区(苏州片区所属辖区)出台文件提出，全职工作的顶尖人才按“一事一议”最高可获得500万元购房补贴。\r\n\r\n此外，3月初，济南发布政策，重点高层次人才可享受最高100万元的购房补贴、最长5年的租赁住房补贴或最长免租5年的人才公寓。同在3月，台州启动2020年度“500精英计划”，新引进的高层次人才可享受最高300万元的购房补贴。\r\n\r\n中新经纬客户端发现，在上述6个地区中，南宁是对此前政策进行调整。4月，南宁印发文件，扩大购房补贴发放范围，此后进一步调整，经认定的D类、E类高层次人才购房补贴不再受引入到南宁的时间限制。按照目前规定，南宁对A类人才予以200万元购房补贴，对B类人才予以120万元购房补贴，对C类人才予以60万元购房补贴，对D类人才予以40万元购房补贴，对E类人才予以20万元购房补贴。\r\n\r\n多地对抗疫医护人员购房契税全额补贴\r\n\r\n资料图 中新经纬 摄\r\n\r\n除了直接规定补贴金额，还有不少地区按照购房价款或缴交契税的比例发放补贴。\r\n\r\n邢台、泸州、南充等地均提出按成交房屋总价的一定比例给予购房补贴。比如，邢台表示，大学本科、高级技师、高级工等急需人才纳入购房补贴范围，符合条件的按个人购房总价款的2%予以补贴。泸州表示，购买自住住房的，按购房款总额一定比例一次性给予置业补助，自2020年3月至2022年2月，不同购买时间补助比例共有3%、2%和1%三种，购房人合计最高补助限额5万元。\r\n\r\n此外，桂林、衡阳、郴州、镇江、玉林、钦州、抚州、资兴等8个地区提出实施购房契税补贴，以已缴契税为基数，补贴比例自30%至100%不等。中新经纬客户端注意到，桂林等4个地区提出，对医护人员购房的给予全额契税补贴。\r\n\r\n其中，桂林表示，抗疫一线医护人员在2020年度购买新建商品房的，按照实际缴纳契税给予100%同等金额补贴。镇江表示，自本通知印发之日(2020年2月21日)起3年内，所有赴湖北医务人员，本人在镇江购房并符合公积金贷款条件的，享受1次个人贷款限额上浮50%及契税全额补贴政策。\r\n\r\n三地文件先发后撤\r\n\r\n值得一提的是，除了上述30多个地区，河南省驻马店市、内蒙古自治区赤峰市和山东省泰安市宁阳县三地年内也曾推出购房补贴政策，不过后来相关文件未被执行。\r\n\r\n公开报道显示，2月21日，驻马店出台文件，首次提出下调居民首付款比例，首套房贷款最低首付比例由30%下调为20%。此外，对在市中心城区、各县城区购买首套居住房屋的给予补助，各类人才补助标准为200元/㎡，大中专以上毕业生补助标准为150元/㎡，农民工补助标准为100元/㎡。2月28日，驻马店市人民政府主要负责同志被约谈，此后该政策文件被撤回。\r\n\r\n4月16日，赤峰推出《疫情防控期间稳定住房消费若干措施》，鼓励个人购房，对个人购买新建商品房住宅的，按其所购房屋实际缴纳税额的50%给予财政补贴。不仅如此，该文件提出，促进商品房预(销)售，加大住房公积金使用力度。三天后，赤峰市人民政府发布通知称，经研究决定，《疫情防控期间稳定住房消费若干措施》不予执行。\r\n\r\n5月15日，宁阳发布文件表示，对购买普通住房(不含二手房)且已签订网签合同备案并实际购买的购买人，按每套5000元的标准进行补贴，补贴期限为自本文发布之日起到2020年10月8日止。此外，取消二手房限售期、降低首付额度、鼓励企业优惠打折销售。两天后，宁阳县人民政府发布通知称，上述文件因部分条款内容与上级有关要求不符，经研究，决定不予执行。\r\n\r\n对于上述情况，易居研究院智库中心研究总监严跃进向中新经纬客户端指出，驻马店出台的政策带有很强的刺激倾向，是当时全国政策力度较为宽松的代表性政策。对于赤峰的政策，严跃进表示，此次政策最大的亮点就是明确提出“鼓励个人购房”的表述，相比全国很多城市相对含蓄的表述，赤峰的政策表述带有很强的托市导向。\r\n\r\n严跃进认为，此前驻马店等城市被约谈，都说明房住不炒是大原则，客观上也有助于明确后续的政策思路，对于各地的政策调整有很强的指导意义。(中新经纬APP)', '2020-06-11 05:33:55', 'wam', '无', '无', 0);
INSERT INTO `news` VALUES (5, '国内新闻', '国产HPV疫苗来了，约吗？', '赵琛', '首批国产HPV疫苗落地，国内多个省市可预约接种。业内人士认为，价格更低廉的国产疫苗有望提高HPV疫苗的接种率，覆盖到更多女性。 ', '作为预防癌症的疫苗，HPV疫苗（人乳头瘤病毒疫苗）一直是备受社会关注的“网红”疫苗。日前，随着价格更有优势、供应量稳定的国产二价HPV疫苗的“落地”，女性想接种疫苗有了更多的选择。\r\n\r\n“网红”疫苗有了国产版\r\n\r\n“价格更低、可能会更好预约，我应该打吗？”32岁的张宁宁在广东湛江工作，5月上旬，一则新闻让她再度关注起HPV疫苗。\r\n\r\n5月18日，全国首针国产二价HPV疫苗（商品名“馨可宁”）在湖北省妇幼保健院接种，一名10岁的武汉女孩成为全国首位接种者。“馨可宁”的上市，打破了依赖进口疫苗的局面。\r\n\r\n国家癌症中心、中国医学科学院肿瘤医院妇科肿瘤科副主任医师雷呈志在接受采访时表示，人乳头瘤病毒被认为是引起宫颈癌等癌症的罪魁祸首，男性和女性都可能感染，适龄和适合人群接种HPV疫苗可预防相关癌症发生。\r\n\r\n“接种疫苗就可以预防癌症，这个信息传开后，有段时间只要碰到个女性朋友就在讨论要不要打HPV疫苗、去哪儿打、如何预约等问题。”在张宁宁看来，HPV疫苗的确是“网红”疫苗，进入国内市场后其话题热度持续了很久。\r\n\r\n中国食品药品检定研究院数据显示，按照3.56亿适龄女性、每人3针计算，我国HPV疫苗市场缺口超过10亿支。\r\n\r\n目前，在我国合法上市的HPV疫苗包括进口二价疫苗、进口四价疫苗、进口九价疫苗以及国产二价疫苗“馨可宁”四种。其中，“馨可宁”价格为329元/针，5月起，首批共计93643支疫苗可预约接种。\r\n\r\n相关部门表示，由于各地疫苗采购供应时间有差异，建议咨询辖区社区卫生服务中心或乡镇卫生院预防接种门诊。\r\n\r\n“价格更便宜，有点心动”\r\n\r\n目前，湖北、新疆、吉林、云南、江苏、山东等地区作为第一批接种地点，已经开始使用“馨可宁”。在社交媒体平台，一些网友开始询问如何预约接种、接种费用等相关问题。对一些年轻女性来说，在确定接种意愿后，“去哪儿打针”是最关心的问题。\r\n\r\n《工人日报》记者在一款疫苗接种预约小程序上查询到，包括武汉、昆明等在内的多个城市已经有国产HPV二价疫苗接种服务。截止到6月3日，以武汉市为例，小程序上共有11个预约接种门诊、社区卫生服务站等机构提供国产HPV疫苗的预约入口，但也有多个界面显示缺货或缺苗；在昆明市，有7个机构在小程序上有预约入口。\r\n\r\n目前，“馨可宁”已在云南保山、大理、昆明等多个地区“落地”，有不少女性在社区服务中心等机构排队登记、接种疫苗。\r\n\r\n“价格比进口疫苗便宜很多，有点心动。”在北京工作的陈怡表示。6月4日，她致电离家最近的社区医院询问，被告知暂无国产HPV疫苗，并且受到疫情影响，打适龄的进口HPV四价疫苗需要等待电话通知，她决定“再等等看”。\r\n\r\n从“一针难求”到批量上市\r\n\r\n谈起HPV疫苗，在北京生活的关云山调侃自己是第一批“吃螃蟹的人”。2017年，得知进口HPV疫苗进入国内市场，她把家附近的社区医院电话打了个遍。2018年春节后的第一个工作日，她如愿接种了第一针进口HPV四价疫苗，“到第三天我朋友再去，就没有苗了”。\r\n\r\n“一针难求”是很多人对HPV疫苗的切身感受。2019年，刚大学毕业的彭欣等了半年才排到进口HPV九价疫苗，赶上了26岁的“末班车”。\r\n\r\n此外，进口HPV疫苗较为昂贵的价格也让一些女性在打与不打之间犹豫不决。“我已经30多岁了，花费几千元打疫苗，不知道效果好不好。”张宁宁对记者说。\r\n\r\n在她看来，相比于2018年前后的HPV疫苗接种热潮，此次国产疫苗上市步伐更稳。“毕竟特别有热情的那批人基本接种了，我们这些还在犹豫的人，可能会更加理性地去选择。”\r\n\r\n业内人士表示，随着国产疫苗的批量上市，可以更好地缓解国内HPV疫苗供应紧张的状况。同时，价格更低廉的国产疫苗，有望提升HPV疫苗的接种率，可以覆盖更多女性。 ', '2020-06-11 05:34:38', 'wam', '无', '无', 1);
INSERT INTO `news` VALUES (6, '国内新闻', '暴雨蓝色预警 今天9省市有大到暴雨局地大暴雨', '中国天气网', '中国天气网讯 中央气象台6月11日06时继续发布暴雨蓝色预警：', '预计，6月11日08时至12日08时，贵州北部、四川东部、重庆、湖北西部、湖南西北部、河南东部、山东南部以及山西北部、陕西北部等地部分地区有大到暴雨，其中，重庆西南部、河南东部等地局地有大暴雨（100～120毫米）。上述部分地区伴有短时强降水（最大小时降雨量20～50毫米，局地可超过60毫米），局地有雷暴大风等强对流天气。\r\n\r\n防御指南：\r\n\r\n1、建议政府及相关部门按照职责做好防御暴雨应急工作；\r\n\r\n2、切断有危险地带的室外电源，暂停户外作业；\r\n\r\n3、做好城市、农田的排涝，注意防范可能引发的山洪、滑坡、泥石流等灾害。', '2020-06-11 05:35:55', 'wam', '无', '无', 0);
INSERT INTO `news` VALUES (7, '军事新闻', '美国要造核动力破冰船还想装上武器 专家：这是个危险信号', '环球时报 ', '美国总统特朗普9日签署一份关于北极和南极安全的备忘录，呼吁美国海岸警卫队研究发展核动力破冰船的可能性，该文件还要求评估未来破冰船将携带什么样的防御性武器，特别是为了抵御来自“实力相当对手”的威胁。中国专家表示，这向外界发出一个危险信号，美国似乎正在考虑以武力采取排他性的方式开发北极资源，这也增大了北极爆发冲突的可能。', '特朗普签备忘录引发关注\r\n\r\nThe Drive网站9日题为“特朗普命令海岸警卫队研究建造‘俄罗斯式’核动力破冰船”的文章称，尽管《维护美国在北极和南极地区国家利益的备忘录》名称听上去非常笼统，但它几乎完全集中在购买破冰船的相关问题上。该备忘录要求美国国土安全部通过海岸警卫队司令部，并与国防部长、海军部长和能源部长合作，“对一种极地破冰船队组合的收益和风险进行研究……使其配置足以满足本备忘录的目标。”\r\n\r\n美未来重型破冰船\r\n\r\n该备忘录中说，美海岸警卫队将进行的研究包括对“尚未签订合同的重型和中型极地安全船（PSC）的扩展作战能力以及相关成本的评估”。这项评估还应评价“足以抵御实力相当对手（美媒文章认为是俄罗斯或中国）”的防御性武器装备，以及使用核动力推进的潜力。报道称，研究核动力破冰船的要求是前所未有的，几乎可以肯定是美国能源部长将参与这项研究的原因。而为了保证自身安全，破冰船也可能安装反舰巡航导弹等武器。\r\n\r\n该备忘录的其余大部分内容涵盖了海岸警卫队一直在做或已经做的事情。包括评估船队的搭配情况。该部门一直表示，希望在获得3艘重型破冰船后，再购买3艘中型破冰船。备忘录还要求根据确定的船队规模和组成，确定和评估至少两个最佳美国基地位置和至少两个国际基地。\r\n\r\n美有能力造核动力破冰船吗\r\n\r\n美媒报道称，美国海岸警卫队和美国海军都未装备过任何形式的核动力破冰船。实际上，迄今为止，这样做的唯一国家是俄罗斯，该国有4艘核动力破冰船，其中包括排水量达2.1万吨的“泰梅尔”号，并计划在数十艘其他常规动力破冰船和有破冰能力的船只基础上，再建造至少5艘核动力破冰船。文章还称，中国也在研制自己的核动力重型破冰船（原文如此——编者注）。\r\n\r\n据俄罗斯《观点报》报道，目前，俄罗斯拥有世界上最大的破冰船队，共有超过40艘各型破冰船。俄罗斯联合造船集团旗下的“波罗的海工厂”宣布，5月26日开始为第四艘“北极”型核动力破冰船“雅库特”号铺设龙骨，它属于22220型核动力系列破冰船，是世界上现有排水量最大、功率最大的破冰船。船长173.3米，宽34米，排水量3.35万吨，破冰厚度达3米，能够在北极条件下护送船队通行，为亚马尔半岛、格达半岛和喀拉海大陆架碳氢化合物矿田原料外运至亚太地区国家市场创造条件。该项目的首艘LK-60Ya“北极号”通用核动力破冰船于去年底开始进行试航。该厂正在建造“西伯利亚”号和“乌拉尔”号两艘量产型核动力破冰船。俄主要核动力破冰船目前包括“亚马尔”号、“胜利50周年”号、“泰梅尔”号、“维加奇”号。\r\n\r\nThe Drive网站称，目前美国海岸警卫队只有两艘破冰船，一艘正在运行的重型破冰船“极地之星”（又译“北极星”号），已经老化并且容易发生重大故障，而另一艘中型破冰船“希利”号至今已有数十年历史。文章称，多年来，美海岸警卫队现有的规模极小的破冰船队迫切需要更新，而经过几十年努力，该部门终于在去年签订第一艘新的常规动力重型破冰船合同。\r\n\r\n俄卫星通讯社10日报道称，俄罗斯预备役海军上校、支持全俄舰队运动主席米哈伊尔·涅纳舍夫表示，美国的资源原则上使他们能够在10年之内建成破冰船队，但面临很多挑战，如美造船公司没有足够经验。此外，美国还需要培训破冰船船队，而俄罗斯已经有数十年的经验。\r\n\r\n或与俄罗斯发生激烈对抗\r\n\r\n特朗普签署的备忘录中提到评估安装防御性武器的可能性。The Drive网站称，未来美海岸警卫队的核动力破冰船有可能与俄罗斯或其他国家的船只发生激烈对抗，特别是在北极地区。这无疑推动了探索美国下一个破冰船潜在防御武器的需求。特朗普的备忘录没有提及海岸警卫队要考虑的任何具体武备方案，但其对最新重型破冰船的要求，为将来可能包含某种武器留出空间。2017年，时任美国海岸警卫队司令保罗·祖库夫特上将也告诉国会议员，海岸警卫队正在考虑在未来破冰船上增加防御和进攻性武器，包括反舰巡航导弹。\r\n\r\n中国专家认为，美国考虑为破冰船安装武器发出了危险信号，这可能迫使更多国家为破冰船安装武器。\r\n\r\n据俄罗斯《消息报》此前报道，俄计划建造新型10510型“领袖”级核动力破冰船，吨位将成为未来世界最大，而且在设计时就预留安装导弹、火炮和反潜装备模块的空间，需要时可迅速转换成核动力导弹破冰船，由于使用新技术，该船破冰厚度可超过4米。▲', '2020-06-11 05:37:17', 'wam', '无', '无', 0);
INSERT INTO `news` VALUES (8, '军事新闻', '美报告宣称中国新添两艘核潜艇 又搬出“中国威胁论”', '环球时报', '据美国《国家利益》网站8日报道，美国最新报告宣称，中国于今年5月新服役两艘094型战略核潜艇，“使其水下核威慑舰队的规模已接近美国的一半”，“使美国本土遭到核打击的风险大增”。', '报道称，这份由美国国会研究服务中心发布的报告题为“中国海军现代化：对美国海军能力的影响”。该报告宣称，战略核潜艇旨在确保本国遭到核攻击时能进行核反击，并“从根本上确保完全摧毁攻击者”。这些“水下幽灵”通常在未公开的海域和极深的海下秘密巡逻。根据美国海军情报办公室的数据，中国新服役的094型战略核潜艇装备有12枚“巨浪-2”潜射洲际导弹，此外还配备了反舰巡航导弹、尾流自导鱼雷、线导鱼雷和水雷等常规武器。尤其让美国海军焦虑的是，中国此前已试射新型“巨浪-3”固体燃料洲际导弹，其射程超过5600英里（约9000公里），“这使美国大陆面临巨大风险”。该报告称，当前美国海军拥有14艘“俄亥俄”级战略核潜艇，“中国战略核潜艇规模已接近后者的一半。预计到2030年，中国战略核潜艇的总数将达到8艘”。此外，该报告还危言耸听地宣称，“中国似乎正在全球扩大其战略核潜艇的活动范围，相关新闻显示全球各热点地区都可见到中国战略核潜艇的踪迹”。\r\n\r\n有分析认为，该报告依然有意炒作“中国威胁”，相比美国战略核潜艇的规模，中国水下核力量仍有很大差距。该报告也承认，美国正计划建造12艘全新的“哥伦比亚”级战略核潜艇，“它们可能是史上最安静的潜艇，中国还无法与之匹敌”。▲（武 彦）', '2020-06-11 05:38:05', 'wam', '无', '无', 1);
INSERT INTO `news` VALUES (9, '军事新闻', '台湾“新战机”成功首飞，号称可对抗解放军，台网友都看不下去了', '徐璐明', '台湾“勇鹰”高级教练机成功首飞，这款被台湾标榜“自造”的战机自曝光以来就备受关注，在首飞成功之后甚至还有绿媒吹捧称这款教练机可以挂弹对抗解放军。', '据“中时电子报”6月10日报道，备受瞩目的台湾“自造”新型高级教练机“勇鹰”在经过多次滑跑测试后，于10日上午9点30分在台中清泉岗机场成功进行了首次飞行测试。\r\n\r\n台湾“勇鹰”教练机首飞画面\r\n\r\n报道称，“勇鹰”教练机的首次试飞并未收起起落架，两架“经国号”IDF战斗机为其伴飞护航，飞行约20分钟后成功降落，该机在接下来的两天还将继续进行测试。\r\n\r\n台军官员表示，“勇鹰”教练机在今年5月完成首飞前的审查，6月开始执行首飞任务。在2026年前，研制方将向台军交付66架“勇鹰”教练机以及26套地面辅助训练系统。\r\n\r\n“勇鹰”教练机自去年9月下旬首次亮相以来，就以“自造”“自主研发”作为标榜，当时台当局就大力鼓吹这款“新”战机。但其实眼尖的台湾网友早就发现，“勇鹰”教练机其实就是缩水版的“经国号”战斗机。当时还有网友吐槽，这款所谓的“新”战机，不就是“经国号”多加了一个座位吗……即使在首飞成功的消息传出后，也有台湾网友批评说“也就是一台IDF改，而且是往下改”。\r\n\r\n“勇鹰”教练机的研制方汉翔公司也承认，“勇鹰”教练机在外形上承袭了“经国号”IDF战斗机的特性。但汉翔坚称，“勇鹰”大量采用了复合材料，超过80%的部分都是全新的设计。\r\n\r\n在“勇鹰”首飞成功的消息传出后，更有绿媒添油加醋，声称这款教练机具备“平战转换”能力，战时能加挂武器，从教练机变成攻击机，加入到“防卫台湾”的任务中。\r\n\r\n实际上，台湾研制的“经国号”战斗机只是一款轻型战斗机，本身的战斗能力就相当有限。而“勇鹰”教练机更可以说是简配版的“经国号”战斗机，本身是作为造价低廉的教练机而设计，拆除了“经国号”战斗机原有的火控雷达和战斗系统，作战能力更是进一步缩水，想要用来“防卫台湾”恐怕是心有余而力不足。', '2020-06-11 05:43:42', 'wam', '无', '无', 4);
INSERT INTO `news` VALUES (10, '财经新闻', '银行理财收益率连续4周上涨 专家称上升空间不大', '证券日报', '理财产品收益“翘头”历来是理财市场年中季末时的“固定戏份”。据第三方机构发布的最新监测数据显示，今年5月份以来，银行理财平均收益率已连续4周小幅上涨。这是否意味着，理财产品收益年中“翘头”的现象今年还会如约而至呢？', '融360大数据研究院分析师刘银平认为，银行理财收益率有希望继续回升，但上升空间不大。\r\n\r\n“目前还没有感受到年中理财产品收益翘头的现象。”一家总部设在南方的某股份制银行理财经理对《证券日报》记者表示，“抵御利率下行风险，一方面是配置中长期理财产品，以锁定长期收益；另一方面，可以配置偏债混合类投资方向的产品”。\r\n\r\n融360大数据研究院监测数据显示，6月1日-6月7日期间，银行理财产品发行量为1643只，环比增长10.42%。其中，人民币非结构性理财产品平均收益率为3.81%，环比上涨2BP。银行理财产品收益率尽管涨幅不大，但已连续四周小幅走高。\r\n\r\n“近期工业生产继续回暖，市场利率也有所回升，自4月中下旬以来，上海银行同业拆借利率shibor，2周至1年期利率持续上升。”刘银平表示。\r\n\r\n交通银行金融研究中心高级研究员武雯在接受《证券日报》记者采访时表示，近期银行理财收益率小幅上升，主要有两方面原因。首先，一般年中理财产品收益率都会小幅上行；其次，由于年中银行考核压力加大，揽储压力加大，在一定程度上推升了理财产品收益率。\r\n\r\n《证券日报》记者走访多家银行发现，目前理财产品收益率基本在3%左右，与之前相比会略高0.1%，收益在4%以上的均是净值型理财产品。\r\n\r\n“去年年中时，银行推出收益相对较高的年中专属理财产品。但今年以来，在持续降息、降准以及资金面流动性宽松的背景下，年中银行不会再有活动，理财产品的预期收益率仍将维持在3%左右，收益率在4%以上的都是净值型理财产品。”一家国有大行网点的理财经理称。\r\n\r\n东方金诚金融业务部助理总经理李茜在接受《证券日报》记者采访时表示，从资产端来看，随着银行净值化转型的持续推进，新发理财产品将以配置固收类产品为主，较难配置高收益的非标资产。同时，考虑到银行存在一定揽储压力，短期内理财收益率下行速度或将放慢，大概率维持现有水平或呈小幅下行。长期来看，未来随着利率市场化和净值化转型的推进，银行理财收益率仍将进一步下降。\r\n\r\n银行理财收益率处于下降趋势，随之而来的是理财转型的加速。银行新发净值型产品数量逐渐增多，银行理财向净值化转型的进程在明显加快。\r\n\r\n据普益标准统计数据显示，截至2020年一季度末，银行非保本理财产品余额约为24.4万亿元，净值型产品存续余额约12.54万亿元，净值型产品余额占比已由新规前的15%大幅提高到目前的51.40%。\r\n\r\n在各类理财产品中，新发固收类净值型产品占据主导，占比超过九成。随着理财子公司新品的发售，混合类甚至权益类产品开始崭露头角。《证券日报》记者调查后发现，在银行理财产品收益整体下行的背景下，通过适量配置权益类资产增厚产品收益成为部分银行特别是理财子公司提供给投资人的新选择。', '2020-06-11 05:44:58', 'wam', '无', '无', 0);
INSERT INTO `news` VALUES (11, '财经新闻', '200倍杠杆做空！空头之王押注港元联汇崩溃，预期收益64倍', ' 中国基金报', '据彭博报道，海曼资本管理公司（Hayman Capital Management）创始人、有沽空之王称号的对冲基金经理Kyle Bass（凯尔·巴斯）已组织一只新基金，押注香港联系汇率制将会失守，通过买入港元下跌期权，他将以杠杆高达200倍，若18个月后，香港联系汇率制安然无恙，投资者将损失所有资金；但如果期间港元下跌幅度40%，将可取得64倍的回报', '证券时报记者就此事向海曼资本管理公司求证，但截至发稿，尚未得到回应。\r\n\r\n近期，港元持续走强并触发7.75强方兑换保证水平，据证券时报记者统计，自今年4月21日以来，香港金管局一共向市场注资443.86亿港元，保持港元币值处于联汇区间水平。\r\n\r\n沽空之王凯尔·巴斯200倍杠杆做空港元\r\n\r\n尽管近期港汇走势呈强，然而一直唱淡港元、有沽空之王之称的对冲基金经理凯尔·巴斯正在启动一项新基金，对赌香港联汇将在接下来的18个月内崩溃。\r\n\r\n彭博报道称，凯尔巴斯已于6月1日成立新基金，以期权合约进行200倍杠杆操作，买入港元下跌期权，对赌港元在18个月内与美元脱钩。巴斯向潜在投资者称，一旦港元脱钩并贬值40%，基金将狂赚64倍，若香港联汇制度在18个月后维持不变，投资者将损失所有资金。\r\n\r\n5月23日，巴斯在Twitter 发文，敦促投资者及时将港元资产转换为美元。\r\n\r\n不过6月10日证券时报记者就做空一事向海曼资本管理公司求证，对方并未回应。\r\n\r\n事实上，做空港元投资行动一直都有，但大部分都以失败告终。最著名的案例，莫过于资本大鳄索罗斯1997年借亚洲金融风暴之际，尝试做空港元，但最终仍以失败收场。\r\n\r\n记者采访多位香港投行以及券商分析师发现，他们观点呈现两极分化，一位外资资深投行人士向记者表示，“从我个人角度来看我不会持有港元。经过此轮疫情，美元汇率指数向上突破102并达到阶段性的高位，与之形成鲜明对比的是，非美元货币的汇率却一路走软，现时港元走强并不代表以后。面对日益加剧的流动性紧缩和全球衰退，美元才是安全避风港，甚至黄金也在日渐失去光芒。”\r\n\r\n中泰国际（香港）分析师颜招骏却持有与之相反的观点，他认为，现时来看，当下攻击港元比1997年更难。截至3月，香港外汇储备有4376亿美元，远多于1997年及2008年的近1000亿美元及1600亿美元，占港元M3货币供应45.7%。总体上，现时香港扞卫联系汇率的实力较此前更强。更重要的是，如今中国外汇储备高达3.23万亿美元，而1997年时只有1399亿美元。他认为，“通常一个做空者，不会公开说已经做空或想做空某个资产，通常是接近平仓那一刻才会公开，希望市场跟随而获得平仓机会。“\r\n\r\n彭博社调查数据显示，市场预期港元在未来12个月，跌穿7.9兑1美元的机会只有6%，有投行人士向记者称，押注港元下跌就好比买彩票，参与者习惯付出小部分资金，希望获得不成比例的高额回报，但机会却微乎其微。\r\n\r\n港元汇率走强，金管局近月已注入444亿资金入市\r\n\r\n联系汇率自1983年实施以来，港元币值一直稳定维持于7.75-7.85兑1美元水平，当港汇触及7.75强方兑换保证，金管局便会在市场卖出港元，当港汇触及7.85弱方兑换保证，局方便会买入港元，达到托价作用。在联汇制度下，港元兑美元并不存在大幅贬值的空间。\r\n\r\n就在6月10日早间，金管局再度承接3.43亿美元沽盘，向市场注资26.58亿港元。6月9日港股收盘后已承接2.5亿美元沽盘，向市场注入19.38亿港元，两日共注入45.96亿港元，银行体系结余将于6月11日增至1184.07亿港元。\r\n\r\n证券时报记者统计数据显示，今年4月21日以来，金管局一共14次入市承接美元沽盘，合计向市场注资达到444亿港元。\r\n\r\n有市场人士认为，金管局密集入市承接美元，正反映资金流入香港，同期网易、京东招股，以及香港股市上扬，资金需求大增也是近期港元走强的原因。\r\n\r\n香港财政司司长陈茂波日前发言称，对香港未来继续汇聚“财气”和“人气”、保持国际金融中心地位充满信心，美国最近提及的所谓制裁措施，相关实际影响非常有限。即使在最极端情况，香港被限制使用美元或美元结算系统又如何？香港是全球第三大美元外汇交易中心，为不少亚太区甚至国际企业提供各种投资、财富管理、贸易和结算服务，与全球经济和金融体系紧密相连。冲击香港金融制度的手段，也会为包括美国在内的全球金融市场带来极大的震荡，甚至削弱国际市场对使用美元及持有美国金融资产的信心。', '2020-06-11 05:45:27', 'wam', '无', '无', 0);
INSERT INTO `news` VALUES (12, '财经新闻', '中概股掀熔断潮！最高涨近1000% 更有先翻倍再腰斩', ' 证券时报网', '美东时间周三，美股三大指数表现分化，纳指史上首次收在10000点关口之上，创盘中与收盘历史新高。\r\n\r\n消息面上，美联储维持联邦基准利率0%-0.25%不变。美联储主席鲍威尔表示，将致力使用所有的工具来应对新冠肺炎疫情对经济造成的冲击。', '纳指收盘首次站上万点大关 中概股掀熔断潮\r\n\r\n美股三大指数周三表现分化。道琼斯指数收报26989.99点，下跌282.31点，下跌1.04%；标普500指数收报3190.14点，下跌17.04点，跌幅0.53%；纳斯达克收盘首次站上万点大关，报收10020.35点，上涨66.60点，上涨0.67%。\r\n\r\n个股方面，亚马逊和苹果股价均上涨逾1.7％，创下历史新高。谷歌母公司Alphabet和流媒体Netflix分别上涨0.9％和0.1％。前期表现优于大盘的受益于经济重新开放的股票普遍下跌。美国航空、联合航空和捷蓝航空的跌幅均超过8％，富国银行下跌近9％，而花旗集团下跌6.1％。摩根大通股价下跌4.1％。特斯拉收盘上涨8.97%，股价站上1000美元，收盘报1025。05元，市值达到1901亿美元，超过丰田汽车，成为全球市值最大的汽车制造商。\r\n\r\n多家中概股盘中再次出现了熔断式的上涨，嘉银金科、稳盛金融、金融界集体熔断，嘉银金科一度暴涨10倍，换手率达到100%，但收盘时涨幅均出现大幅回落。截至收盘，稳盛金融上涨170.8%，嘉银金科、青岛天地荟涨幅超过90%，金融界涨幅超过50%。\r\n\r\n前一交易日涨幅高达394%的房多多盘前涨幅一度达到100%，但开盘后开始放量下跌，收盘下跌66.38%，股价已经跌回飙升前水平。值得注意的是，房多多最新公布的财报并未出现惊喜：公司一季度营收2.72亿元，同比下降58.5%；归母净利润亏损1.36亿元，去年同期亏损5143.9万元。公司预计二季度营收在6.6-7.2亿元之间。\r\n\r\n关于中概股近期的大幅波动，市场并没有太多统一意见。彭博社就认为：“近期市场的波动性大幅增加，但目前尚不清楚是什么原因引发了失控的走势。”\r\n\r\n商品市场方面，截止北京时间周四早上6：00，纽约商品交易所的黄金和白银分别上涨1.51%和3.1%。\r\n\r\n美联储按兵不动\r\n\r\n消息面上，北京时间周四凌晨，美联储发布最新声明，公开市场操作委员会（FOMC）决定将基准利率维持在0%-0.25%不变，将超额准备金率（IOER）维持在0.1%不变，且至少维持当前速度增持国债与资产支持证券（MBS），目标是每月购买800亿美元的美国国债，以及400亿美元的抵押支持证券（MBS）。\r\n\r\n在随后的新闻发布会上，美联储主席鲍威尔表示，当前货币政策立场是适宜的，美联储根本没有考虑加息，长期通胀预期相当稳定。当前美国经济形势和大萧条之间有很多区别，FOMC预计经济将在今年下半年复苏。对于当前美国各地爆发的抗议种族歧视问题，鲍威尔称美国社会和美联储内部都容不下种族主义，美联储重视货币政策平等对待美国民众这个问题。\r\n\r\n对于就业数据，鲍威尔称美国5月非农就业报告的数据有所改善，但不确定是否已经在5月份见底、要恢复至美国爆发新冠疫情之前的水平还有很长的路要走，未来数年内可能都会有至少数百万人无法找到工作，疫情对劳动力市场的影响“令人心碎”。对于美联储是否有更多措施来刺激经济，鲍威尔称联储讨论了收益率曲线控制（YCC）这个选项，它的效果仍然是一个“开放式问题”，未来将继续讨论，而主街贷款（Main Street）计划的规模仍然可能进行调整。\r\n\r\n在美联储公布的联邦储备委员会成员和联邦储备银行行长对货币政策独立评估的经济预测表格中。在宏观经济数据方面，对2020全年GDP的增速最差的预测是下降10%，但大多数成员预测2021年经济增长将回升正常水平。而失业率同样将在2021年好转。\r\n\r\n对于基准利率，19位成员有17位预计将维持利率在当前水平直至2022年年底，只有两位美联储政策制定者预计联邦基金利率将在2022年从零水平上调。\r\n\r\n美联储声明全文：\r\n\r\n美联储致力于在这一充满挑战的时期内使用各种工具来支持美国经济，从而促进其最大就业和价格稳定的目标。\r\n\r\n新冠病毒的爆发正在美国和世界各地给人类和经济造成巨大困难。该病毒和为保护公众健康而采取的措施已导致经济活动急剧下降和失业人数激增。需求疲软和油价大幅下跌抑制了消费者价格通胀。当前金融状况有所改善，部分反映了支持经济的政策措施以及信贷向美国家庭和企业的流动。\r\n\r\n持续的公共卫生危机将在短期内严重影响经济活动，包括就业下降和通货膨胀，并在中期对经济前景构成相当大的风险。鉴于这些事态发展，委员会决定将联邦基金利率的目标范围维持在0％至0.25％。委员会希望维持这一目标范围，直到它有信心经济经历了最近的事件，并有望实现其最大的就业和价格稳定的目标。\r\n\r\n委员会将继续监测对经济前景影响的信息，包括与公共卫生、全球发展和通货膨胀压力有关的信息，并将使用各类工具和采取适当行动支持经济。在确定未来对货币政策立场进行调整的时机和规模时，委员会将评估与其最大就业目标及其对称的2％通胀目标相关的已实现和预期经济状况。这项评估将考虑到广泛的信息，包括劳动力市场状况的衡量标准、通货膨胀压力和通货膨胀预期的指标以及有关金融和国际发展的指数。\r\n\r\n为了支持信贷流向家庭和企业，未来几个月，美联储将至少以目前的速度增加其持有的美国国债，机构住宅和商业抵押贷款支持证券的数量，以维持平稳的市场运作，从而促进有效的市场传导。货币政策扩大了金融条件。此外，开放市场服务台将继续提供大规模的隔夜和定期回购协议操作。委员会将密切监测事态发展，并准备适当调整其计划。\r\n\r\n投票赞成此次货币政策行动的委员包括：主席杰罗姆·鲍威尔、副主席John C． Williams、 Michelle W． Bowman、Lael Brainard、Richard H． Clarida、Patrick Harker、Robert S． Kaplan、Neel Kashkari、Loretta J． Mester和Randal K． Quarles。', '2020-06-11 05:46:13', 'wam', '无', '无', 0);
INSERT INTO `news` VALUES (13, '财经新闻', 'A股三大指数收跌 稀土与特斯拉概念股逆市走强', ' 东方财富研究中心', '【收盘播报】A股三大指数今日午后走弱，最终集体收跌，市场成交量放大，两市合计成交7210亿元，行业板块多数收跌，稀土与特斯拉概念股逆市走强。北向资金今日小幅净流入13.90亿元。', 'A股三大指数今日午后走弱，最终集体收跌，市场成交量放大，两市合计成交7210亿元，行业板块多数收跌，稀土与特斯拉概念股逆市走强。北向资金今日小幅净流入13.90亿元。（点击查看沪深港通资金流>>）\r\n\r\nK图 BK0578_0\r\n\r\nK图 BK0644_0\r\n\r\n　　具体来看，沪指收盘下跌0.78%，收报2920.90点；深成指下跌0.81%，收报11243.62点；创业板指下跌0.29%，收报2195.64点。\r\n\r\n　　对于后市大盘走向，机构纷纷发表看法。\r\n\r\n　　兴业证券认为，2020年下半年市场在震荡中蛰伏，短期投资把握结构性生机，长期的全面性的生机正在孕育中。整体来看，2020年下半年市场呈震荡格局，经济基本面处于内需向上，外需向下的交错期，流动性维持宽松但不“泛滥”，风险偏好反复冲击、折腾，把握结构性机会。一方面，国内经济在政策催化和复工复产加速下逐步回归正常，经济和企业盈利增速逐季提升；另一方面，国外，特别是欧美国家疫情拐点逐步出现，逐步着手恢复经济，但整体需求较弱，恢复还需一定时间。同时，随着美国大选，中美关系可能面临更大压力，亚非拉疫情等尚在发酵。\r\n\r\n　　“这些因素正在制约着市场投资者的情绪，待这些因素逐步随着市场震荡调整过程中price in，而内部流动性整体宽松、政策托底和改革红利频出的利好，A股蛰伏之后有望生机勃勃。”兴业证券指出。\r\n\r\n　　光大证券对大势判断是：“货币是估值的锚，市场仍低估。”从中长期的角度看，在疫情这种自然灾难的冲击下，货币而非盈利是估值更合适的锚，具体而言，股市市值/M2将比PETTM更适合衡量外生灾难冲击下的市场估值水平。以2011年1月1日至2020年5月24日为考察区间，目前A股市值/M2为27.9%、历史估值分位为43.7%，仍处于低估区间。\r\n\r\n　　申万宏源对大势判断上相对乐观，“下半年逐步转向战略乐观。新冠疫情带来更彻底的出清；居民储蓄率上升，配置需求释放，我们有理由开始期待牛市。全球都是大病初愈，我们期待未来，也敬畏未来。”\r\n\r\n　　中信证券研报认为，A股将在下半年开启中期上行的“小康牛”，政策驱动基本面修复，A股在全球配置吸引力提升，宽松的宏观流动性向股市传导，三大因素将共同驱动A股上行。海外因素在三季度的扰动依然比较频繁；政策发力下国内经济将在三季度快速恢复至正常水平，A股盈利增速将逐季抬升；全年流动性整体依然宽松，明年会逐步回归常态。预计三季度后期，A股将开启一段持续数月的趋势性上涨行情。\r\n\r\n　　国信证券对A股市场维持相对乐观的观点，行业配置方面内需板块确定性较高。首先，下半年各类扩大内需政策加速落地；其次，宽货币和宽信用的流动性环境将逐步得到确认；最后，以A股为代表的新兴市场将持续受益于全球流动性宽松的大环境。此外，从全球主要指数估值水平所处位置来看，A股整体估值水平在历史中位水平，股票风险溢价仍相对较高，后续市场风险偏好存在修复的机会。', '2020-06-11 05:48:20', 'wam', '无', '无', 0);
INSERT INTO `news` VALUES (14, '财经新闻', '市场战术性调整不改反弹趋势 下半年上车机会出现', ' 和信投顾', '市场短期虽有调整需求，但整体上保持对下半年行情的乐观态度。建议投资者战略性配置券商、大消费、新基建的龙头标的，短期可参与园区开发、汽车产业链、消费家电、云游戏、传媒板块交易性机会。', '今日两市相对平开后，沪指尝试再度进攻3000点未果，获利盘与套牢盘集体出逃，三大指数应声跳水，个股出现明显分化，赚钱效应萎缩。盘面上，券商板块铁肩担道义，代表权重股力挺大盘，汽车产业链、计算机应用、通讯服务、园区开发涨幅居前，游戏概念上演涨停潮表现最为耀眼，农林牧业、消费零售、家用电器则展开调整。技术层面，沪指3000点久攻不下，多方的锐气被蚕食，主力资金分歧加大，久盘必跌魔咒如约应验，MACD红柱萎缩，量能保持在低位，回踩蓄力选择调仓换股成短期共识。\r\n\r\n　　当前的全球资本市场格局不确定因素增加，以美股为首的海外市场风险值得重点关注，隔夜外盘剧烈波动，中概股甚至出现熔断潮，伴随着纳斯达克创出历史新高而来的是苹果、亚马逊、微软、特斯拉等巨头的股价被吹上天，泡沫仍在进一步放大，可形成鲜明对比的是美国经济的疲弱，似乎已经有股“郁金香”的味道了，需警惕海外风险集中释放波及国内市场。从内部的角度来看，在国内货币政策维持宽松的背景下，实际利率走低趋势难以逆转，大量流动资金缺乏较好的投资标的，权益性资产近期受到热捧爆款基金频现，政策上也对产业资本、信托等入市有一定松绑，未来资本市场体制改革带来的红利有望得到释放，下半年预计还有千亿级资金流入。市场短期虽有调整需求，但整体上保持对下半年行情的乐观态度。建议投资者战略性配置券商、大消费、新基建的龙头标的，短期可参与园区开发、汽车产业链、消费家电、云游戏、传媒板块交易性机会。\r\n\r\n　　【消息面】\r\n\r\n　　1、上海自贸区临港新片区管委会与中国重燃签署战略合作协议\r\n\r\n　　6月11日讯，今日上午，中国(上海)自由贸易试验区临港新片区管委会与中国联合重型燃气轮机技术有限公司签署《战略合作协议》，决定携手打造国家科技重大专项和临港新片区共同创新发展的典范。目前，中国重型燃气轮机试验验证基地拟选址于临港新片区前沿装备制造区域，毗邻申能集团临港燃机电厂160万装机，属于上海市规划的480万装机的电源基地。\r\n\r\n　　2、腾讯推动斗鱼虎牙合并最快或今年底明年初\r\n\r\n　　6月11日讯，据新京报，多位接近交易的人士证实，斗鱼、虎牙的合并已经在路上了，推动其合并的建议由腾讯提出，具体细节未定。虎牙内部人士称，“最快可能今年底、明年初，在这之前虎牙还将迎来新高层，以及新战略目标”。', '2020-06-11 05:48:57', 'wam', '无', '无', 0);
INSERT INTO `news` VALUES (15, '财经新闻', '欧洲股市大幅下跌 美股道指期货跌近2%？', '东方财富网', '美国三大股指期货震荡走低，截至发稿，道指期货下跌1.80%，纳指期货下跌1.18%，标普500指数期货下跌1.40%。', '6月11日，欧洲股市主要指数大幅下跌，截至发稿，欧洲斯托克50指数下跌2.10%，德国DAX指数下跌2.27%，英国富时100指数下跌2.22%，法国CAC指数下跌2.46%。\r\n\r\nR YM00Y_0\r\n\r\nR NQ00Y_0\r\n\r\nR ES00Y_0\r\n\r\n　　美国三大股指期货震荡走低，截至发稿，道指期货下跌1.80%，纳指期货下跌1.18%，标普500指数期货下跌1.40%。\r\n\r\n　　延伸阅读：\r\n\r\n　　多项数据黯淡 欧洲经济“弱势重启”\r\n\r\n　　疫情对欧洲经济的冲击仍在持续，多国政府相继推出经济扶持新举措。业内人士认为，目前欧洲经济触底反弹势头十分微弱，复苏之路面临诸多挑战。\r\n\r\n　　经济数据不容乐观\r\n\r\n　　德国联邦统计局9日发布数据显示，德国4月货物贸易出口额较去年同期减少31.1%，为1950年以来最大降幅；进口额较去年同期减少21.6%。4月出口与进口额环比分别减少24%和16.5%，均创下自1990年8月以来最大环比降幅。\r\n\r\n　　统计局表示，欧洲各国关闭边境、全球旅行限制、供应链中断和国际物流受到冲击，都对德国出口造成重创。值得注意的是，贸易伙伴进口萎缩是德国出口暴跌的主要原因。相较去年4月，德国对法国、意大利和美国的出口降幅分别达到48.3%、40.1%和35.8%。\r\n\r\n　　德国商业银行经济学家拉尔夫·索尔文认为，随着生产活动恢复，5月出口额将显著增加，但要恢复到疫情前水平还需要一段时间。\r\n\r\n　　德国工业联合会表示，预计今年设备投资将下滑20%左右，民间消费将萎缩7%，出口将下降15%左右。\r\n\r\n　　欧盟统计局9日公布，经修正后，欧元区19个成员国今年第一季度国内生产总值(GDP)环比下降3.6%，同比下降3.1%。虽然下降幅度小于预期，但仍为1995年以来的最大降幅。\r\n\r\n　　欧盟统计局表示，受疫情影响，一季度家庭支出和投资暴跌，大大拉低了欧元区的GDP。其中，家庭消费支出下降4.7%，在GDP总量下降中占2.5%；投资下降4.3%，在GDP总量下降中占1%。\r\n\r\n　　法兰西银行6月9日发布的季度报告显示，到今年第四季度，法国就业量预计将会比去年年底的就业量低100万左右。法兰西银行行长表示，法国经济从3月衰退以来已经快速恢复，但距离走出困境仍然十分遥远。此外，法兰西银行还预计，由于“限制令”的实施，法国今年第二季度GDP将下降15%。\r\n\r\n　　此外，瑞士联邦经济部9日宣布，瑞士5月失业率从4月的3.3%升至3.4%，已达到2017年2月以来最高水平。另据瑞士企业联合总会预测，如果不进行第二轮“封锁”，瑞士今年GDP将同比下降5.6%，今明两年失业率分别达3.8%、4.3%。\r\n\r\n　　各国加大扶持力度\r\n\r\n　　为应对经济下滑和救助可能出现破产危机的企业，欧洲多国政府纷纷出台针对性举措，继续加大对本国经济的扶持力度。\r\n\r\n　　法国9日宣布了一项约150亿欧元的航空业救助计划。除了此前已经宣布的对法国航空公司的70亿欧元援助，新的救助计划还包括一项启动资金为5亿欧元、预计将达10亿欧元的投资基金，以支持中型企业的发展。此外，法国国防部和内政部也采取措施支持航空业。法国国防部日前宣布了6亿欧元航空订单，法国内政部也计划订购2亿欧元的航空器。\r\n\r\n　　当地时间8日，意大利政府对外发布出口振兴计划，提出为应对疫情导致的全球贸易放缓，将整合所有相关政府部门和经济力量，进一步打造国家体系支持“意大利制造”企业，并为促进意大利企业国际化、开展相关推广活动提供14亿欧元资金。\r\n\r\n　　意大利外长迪马约表示，振兴计划将重点发展传播、培训和信息、电子商务、展会体系、整合推广和金融支持六个支柱领域。意大利经济财政部部长瓜尔蒂耶里指出，今后应着力增强数字化、虚拟和电子商务等领域的推广能力。\r\n\r\n　　为应对疫情冲击，葡萄牙政府9日批准总额达130亿欧元的追加预算案。葡萄牙总理科斯塔表示，这笔追加预算将用于全部或部分减免受疫情影响公司的企业所得税、发放收入受影响的企业员工工资、支付失业救济金等。\r\n\r\n　　同日，瑞士也加大了经济扶持力度。瑞士联邦议会联邦院和国民院就减免商户房租达成一致，具体措施包括：月租金不高于两万瑞郎的商户只需支付强制停业期间40%的房租，其余60%由业主承担，因疫情必须减少商业活动的商户也可申请一定的租金优惠。联邦委员会将为业主提供2000万瑞郎的资金援助。\r\n\r\n　　复苏之路依然崎岖\r\n\r\n　　据英国《金融时报》报道，欧洲人重新开始上班、购物和外出就餐，说明疫情管制措施造成的最严重经济损失阶段已经过去。不过，总体活动仍远低于正常水平，意味着欧洲经济复苏之路依然崎岖。\r\n\r\n　　报道称，失业率高企和家庭收入下降可能加剧经济裹足不前的状况。牛津经济咨询公司经济学家罗西·科尔索普表示，就整个欧洲而言，“高度的不确定性和就业前景疲软，意味着消费者也许会选择把钱存起来而不是花掉”。\r\n\r\n　　3月以来，欧洲央行通过扩大量化宽松、实施长期再融资操作等系列举措，为欧元区提供大量流动性支持。欧洲央行6月4日决定，将应对疫情的紧急资产购买计划规模由7500亿欧元扩大至1.35万亿欧元，购债至少持续至2021年6月。有经济学家担心，在经济活动足以支撑更多就业之前，这些扶持资金就可能耗尽。\r\n\r\n　　据路透社报道，目前欧洲央行正筹划设立一个坏账银行，以应对疫情发生后可能出现的数千亿欧元坏账。据官方统计数据，欧元区内被认为不太可能全额偿还的贷款规模已经超过5000亿欧元，其中包括信用卡贷款、汽车贷款和按揭贷款。\r\n\r\n　　凯投宏观首席经济学家尼尔·希认为，欧洲经济开始触底反弹，但经济恢复势头十分微弱。欧洲知名智库布鲁盖尔研究所的研究报告指出，受疫情冲击，欧洲国家经济下行，税收减少，失业率上升，社会福利开支增加，赤字高企，仅仅依靠财政货币强力刺激可能会给财政平衡造成新挑战。(来源：经济参考报)', '2020-06-11 05:49:33', 'wam', '无', '无', 1);
INSERT INTO `news` VALUES (16, '财经新闻', '特斯拉、宁德时代带来双喜临门 产业链概念股起飞', ' 东方财富网', '新能源产业链利好不断，近期大众入股江淮集团和国轩高科，宁德时代超级电池研制成功，市场人士把特斯拉的股价暴涨看做一个历史性时刻，代表了电动化以及智能化革命的胜利，代表了“造车新势力”对于百年燃油品牌的颠覆；目前体现在市值上，未来将体现在业绩上。', '截至美东时间6月10日，特斯拉股价报收1025.05美元，市值超越丰田成为全球第一大车企，正式开启新能源汽车新的时代大幕！而随着技术的不断升级和政策利好的释放，国内新能源汽车产业也将迎来黄金期，目前机构普遍看好，北上资金加码特斯拉产业链个股。\r\n\r\n　　纳指首次站上10000点再创新高，科技巨头同创历史新高\r\n\r\n　　周三，美股收盘涨跌不一，美联储维持基准利率不变，道指跌超1%，报26989.99点；纳指收涨0.67%，报10020.35点；标普500指数收跌0.53%，报3190.14点。\r\n\r\n　　其中，纳斯达克指数首次报收1万点续刷历史新高，苹果、微软、亚马逊等科技巨头均刷新历史新高，4家公司总市值突破万亿美元。具体来看，苹果收涨2.57%，股价报352.84美元，总市值达1.53万亿；微软收涨3.71%，股价报196.84美元，总市值达1.49万亿美元；亚马逊收涨1.79%，年初以来累计上涨40%，股价报2647.45美元，总市值达1.32万亿美元；谷歌收涨0.67%，股价报1465.86美元，总市值回到1万亿美元关口。\r\n\r\n　　特斯拉股价首破1000美元，超越丰田成为全球市值最高车企\r\n\r\n　　截至收盘，特斯拉涨8.97%，股价报1025.05美元，总市值达1901亿美元，超越丰田汽车的1812亿美元，成为全球市值最高的车企！\r\n\r\n　　直接推动因素，或许与特斯拉CEO埃隆·马斯克盘前宣布启动电动卡车Semi的“量产”不无关系。据悉，马斯克在一封内部电子邮件中称，在疫情导致的工厂短暂停产后，特斯拉正在加大汽车产量。如今，是时候开始量产“Semi”半挂式电动卡车了。“电池和动力总成的生产将在内华达州Gigafactory工厂进行，而其他大部分工作可能会在其他州进行。”不过，马斯克并未透露Semi的具体量产日期。\r\n\r\n　　早在今年1月，特斯拉的市值首度突破1500亿美元，超越大众汽车集团位列全球第二，随后，受全球疫情的影响，特斯拉股价一路下跌，自3月底开始反弹，不仅收复失地还创造新高，至6月10日，终于实现了对丰田汽车的超越，成为全球市值最高车企。这或许并不仅是新能源车企对传统车企的颠覆，更可能是新能源时代对化石能源时代发起的强力冲击！2020年的每一天都在见证历史，新能源汽车新的时代大幕正式开启！\r\n\r\n　　据2020年第一季度财报，特斯拉共生产10.3万辆汽车，交付8.84万辆新车，是历史最好水平。总营收为59.85亿美元，同比增长32%；实现净利润1600万美元，调整后每股盈利0.29美元，高于市场预期的每股亏损0.36美元。这是特斯拉首度在第一季度实现盈利，同时也是连续第三个季度实现盈利。\r\n\r\n　　今年以来，特斯拉股价涨幅已达138%。\r\n\r\n　　宁德时代推出16年长寿命电池，股价走势或复制特斯拉\r\n\r\n　　近日，国内新能源汽车动力电池龙头企业宁德时代宣布将推出一款新型长寿命电池，循环充电总续航(总里程)可达200万公里、寿命达16年，成本相比当前电池增加不超过10%，目前已经可以接受订单并进行生产。而目前市面上大部分电动车，其官方保证的电池可使用的总里程数是25万公里、寿命为8年。\r\n\r\n　　宁德时代方面表示，该款新型电池采用自修复长寿命技术，核心在于减缓容量衰减速度，即控制活性锂消耗速度。这项技术通用于三元材料和磷酸铁锂材料，已经具备量产能力，能够缓解用户里程衰减的痛点，大幅降低电池使用成本，提升电动汽车相对于燃油车的经济性。\r\n\r\n　　市场传言宁德时代该款电池将独供特斯拉。对此，证券时报·e公司报道称，宁德时代负责人表示目前是“只要下订单，就可以生产”，换言之，该电池供货不限于特斯拉。宁德时代董事长曾毓群在接受媒体采访时表示：“宁德时代和特斯拉的关系在加强，现在已经在谈‘无钴’电池的合作了。”\r\n\r\n　　业内人士表示，电池企业已经开始通过新技术比拼开启新一轮跑马圈地，宁德时代推出的新型长寿命电池若能顺利应用并普及将进一步突围。目前来看，宁德时代已再次受到资本市场关注，截至6月10收盘，宁德时代股价上涨1.77%，报收156.68元/股，总市值达3458亿元。观察宁德时代近期的股价变动情况不难发现，其走势与特斯拉如出一辙。\r\n\r\n　　新能源汽车行业利好不断，机构普遍看好，北上资金加码特斯拉产业链\r\n\r\n　　新能源行业近期利好频现。今年4月，四部委宣布新能源汽车补贴政策与购置税优惠延长两年，5月数据新能源车批发量达到70199辆，环比上升19%，其中特斯拉国产Model 3销量11095辆，环比增长205%，增速一骑绝尘。6月，先行示范区深圳开始加大对新能源汽车的推广力度。\r\n\r\n　　6月10日晚，深圳市政府发布了《深圳市应对新冠肺炎疫情影响促进新能源汽车推广应用若干措施》，放宽新能源车指标申请条件、扩大新能源车增购范围以及加大推新能源车的推广补贴力度。措施主要分为5大方面，分别为：\r\n\r\n　　放宽个人新能源小汽车增量指标申请条件：放宽指标申请条件主要针对非深户籍人员、华侨、港澳台居民以及外国人进行优待，允许他们在措施实施期间申请深圳市小汽车新能源指标。同时将暂停非深户籍人员 (连续缴纳24个月基本医保)、华侨、港澳台居民(2年累计居住9月以上)以及外国人(签证满2年居住累计9个月以上)的人申请指标限制。\r\n\r\n　　扩大个人增购新能源小汽车车型范围：而扩大车型范围主要针对名下仅有1辆深圳市登记的小汽车或仅持有1个有效指标的个人消费者，放宽购车限制范围，允许这类人群购买纯电动小汽车和插电式混合动力小汽车。\r\n\r\n　　对个人新购新能源小汽车给予综合使用财政补贴：同时对于新购新能源汽车的消费者将给予综合补贴，其中新购纯电动高级型或经济型补贴2万/车，新购插电式混合动力高级型乘用车补贴1万元/车。需要注意的是，购车的店铺需要为在深圳注册的汽车销售企业，且发票中的售卖单位须为深圳市注册登记企业。\r\n\r\n　　对个人置换更新新能源小汽车实施财政补贴：对于目前已经拥有深圳号牌的新能源车用户，置换新车也将给予补贴。置换纯电动高级型或插电式混合动力高级型乘用车将给予2万元/车的补贴，置换纯电动经济型乘用车补贴1万元/车。同上，销售企业也必须为深圳注册的汽车销售企业。\r\n\r\n　　加大新能源汽车停车优惠力度：该措施表明将对深圳市新能源汽车在全路段内停车位，减免首两小时的临时停车费。或每日首次停车时间1小时以内(包括1小时)的，第2次停车免1小时临时停车费。\r\n\r\n　　在利好政策的不断驱动下，机构普遍看好新能源车未来发展，认为中国企业能充分享受新能源汽车爆发增长带来的红利。\r\n\r\n　　中银证券表示，国内新能源汽车补贴政策延续，需求率先复苏；海外疫情控制取得阶段性成效，政策利好不断，有望迎来需求拐点，销量有望逐月回升，看好新能源汽车海外高端供应链标的。\r\n\r\n　　东方证券表示，新能源汽车对传统车的竞争优势得到了突出的体现，同时2020年初爆发的疫情加速了产业调整，此前坚定5-6月是新能源汽车板块配置的左侧和底部窗口期，而随着6月份到来以及产业链的变化，从下半年开始，产业链将迎来黄金十年的右侧配置机会。\r\n\r\n　　华西证券认为，特斯拉国内市场布局进程持续超预期，看好全年及未来销售表现。上海工厂产能不断扩张，Model 3长续航版本提前下线交付，叠加磷酸铁锂版Model 3以及Model Y未来将陆续上市，核心供应商有望持续受益。\r\n\r\n　　另外，证券时报记者注意到，近来，北上资金持续流入A股，对特斯拉概念尤其青睐。北上资金向来以长期价值投资、盈利能力强，投资风格稳健著称，常常被市场称为“聪明钱”，甚至在A股拥有很强的跟风效应。记者继前几日梳理了特斯拉产业链A股上市公司之后(详见：完美V型！特斯拉年内暴涨120%，国产Model 3销量太火，A股小伙伴名单请收好)，又梳理了北上资金近一个月来(5月6日-6月9日)对特斯拉概念股的增持情况。记者发现，获增持数量最多的是三花智控，近一个月北上资金累计增持5549.33万股，北上资金持股占其总股本的6.69%。三花智控是新能源汽车热管理系统全球龙头供应商，最新市值为689亿元。另外，宝钢股份、蓝思科技、长信科技、亿纬锂能、华域汽车等也获得北上资金千万股以上的增持。', '2020-06-11 05:50:50', 'wam', '无', '无', 4);
INSERT INTO `news` VALUES (17, '时政新闻', '宁夏行 百姓福祉是总书记始终不变的牵挂', '邓志慧 宋子节 方开燕', '6月8日至10日，习近平总书记在宁夏考察调研。这是全国两会之后习近平的首次地方考察。此次考察途中，习近平访社区、下田间、问民情、察冷暖，不论走到哪里，百姓福祉是他始终不变的牵挂。', ' 让生活富起来 “脱贫、全面小康、现代化，一个民族也不能少”\r\n\r\n“小康路上一个都不能掉队！”此次宁夏之行，有多个考察点都与脱贫攻坚紧密相关，对宁夏决胜全面建成小康社会，决战脱贫攻坚等工作，习近平有诸多悉心关切与殷殷嘱托。\r\n\r\n6月8日下午，习近平在吴忠市红寺堡镇弘德村考察调研 来源：新华社\r\n\r\n习近平在吴忠的第一个考察点，就是红寺堡区的一个移民村——弘德村。村里现有1699户、7013人，大都是从原州区、同心县等地迁居而来。将这个村落作为此次考察的第一地点，凸显决战决胜脱贫攻坚在习近平心中的重要地位。\r\n\r\n心系民生，设身处地为村民着想——在弘德村，习近平察看扶贫车间，还与回族村民刘克瑞一家聊起家常。习近平说，乡亲们在家门口就业，虽然收入不比进城务工高，但省去了住宿、伙食、交通等费用，还能照顾家庭，一举多得。\r\n\r\n6月8日下午，习近平来到宁夏吴忠市利通区金花园社区考察。来源：新华视点\r\n\r\n小康路上，五十六个民族当携手共进——在吴忠市利通区金花园社区，习近平了解社区党建工作、民族团结社区创建等。他深情对大家说，各民族都是中华民族大家庭的一份子，脱贫、全面小康、现代化，一个民族也不能少。\r\n\r\n6月9日，习近平在银川考察调研 来源：新华社\r\n\r\n完善机制，保障获得长效稳定的利益——在贺兰县稻渔空间乡村生态观光园，习近平沿着田埂走进稻田，同劳作的村民们亲切交谈。他强调要探索建立更加有效、更加长效的利益联结机制，确保乡亲们持续获益。\r\n\r\n“机制建设能够保障高质量脱贫，提升脱贫攻坚治理效能，确保脱贫攻坚与乡村振兴有效衔接。”北京大学贫困地区发展研究院院长雷明接受人民网采访时表示，“要不断创新扶贫机制，例如宁夏脱贫过程中闽宁协作发挥了重要作用，值得其它贫困地区学习借鉴。”\r\n\r\n从1997年到2020年，24年里习近平4次考察宁夏，多次强调脱贫攻坚致胜之道，为宁夏发展指明方向、鼓足干劲。移民搬迁安置、因地制宜发展脱贫产业、东西部扶贫协作和对口支援……随着一条条政策落实落地，民生福祉持续改善，老百姓的幸福感和获得感日益增强。\r\n\r\n让环境美起来 “守好改善生态环境生命线”\r\n\r\n夏日的塞上江南草木葱茏。宁夏山水间，习近平多次加强生态环境保护，擘画美丽新宁夏，绿水青山换得人民幸福感。\r\n\r\n6月8日下午，习近平来到黄河吴忠市城区段，了解加强黄河流域生态保护情况 来源：新华视点\r\n\r\n一条河，凝聚大国千秋大计。6月8日下午，习近平来到黄河吴忠滨河大道古城湾砌护段，沿岸边步道，察看黄河生态治理保护状况。他强调，黄河是中华民族的母亲河，是中华民族和中华文明赖以生存发展的宝贵资源。\r\n\r\n对宁夏治理黄河，他提出这样的要求——坚持综合治理、系统治理、源头治理，明确黄河保护红线底线，统筹推进堤防建设、河道整治、滩区治理、生态修复等重大工程，守好改善生态环境生命线。\r\n\r\n一座山，筑起西北地区重要生态安全屏障。6月9日，习近平来到贺兰山东麓，了解贺兰山生态环境保护情况。\r\n\r\n习近平指出，贺兰山是我国重要自然地理分界线和西北重要生态安全屏障，维系着西北至黄淮地区气候分布和生态格局，守护着西北、华北生态安全。他要求宁夏要加强顶层设计，狠抓责任落实，强化监督检查，坚决保护好贺兰山生态。\r\n\r\n让黄河成为造福人民的幸福河，让贺兰山成为生态平衡的“守护山”。治理好黄河，能够有效解决群众关心的防洪安全、饮水安全、生态安全等问题；保护好贺兰山生态，在防风固沙、涵养水源、生物多样性维护等方面发挥着举足轻重的作用。\r\n\r\n山水之间，寄予着习近平对自然生态保护的厚望，以绿水青山换得百姓幸福感。今年到各地考察，生态保护一直是习近平着重强调的重要议题。在杭州西溪国家湿地公园，习近平沿着绿堤、福堤，察看湿地保护利用情况；在陕西秦岭牛背梁，习近平强调要当好秦岭生态卫士，决不能重蹈覆辙，决不能在历史上留下骂名；在山西，习近平提出了“治山、治水、治气、治城”的“四治”要求……\r\n\r\n“可以看到，习近平总书记持续不断思考生态保护问题，一条从理念到实践层面的路径规划正在清晰呈现；生态保护的提法、内涵正在不断丰富、完善、提高。”中国社科院农村发展研究所研究员、生态经济研究室主任于法稳接受人民网采访时指出，“生态保护的方向愈发明确，逻辑愈发清晰。”\r\n\r\n在于法稳看来，一方面，良好的生态环境是最公平的公共产品，是最普惠的民生福祉；另一方面，生态保护为地方脱贫带来很好的机遇，“利用良好环境推动生态旅游，形成‘人养山、山养人’‘人养水、水养人’的良性循环。”\r\n\r\n让产业强起来 “推动经济发展实现量的合理增长和质的稳步提升”\r\n\r\n6月9日下午 习近平来到贺兰县稻渔空间乡村生态观光园 来源：新华社\r\n\r\n微风轻拂，秧苗摇曳，一派生机勃勃。6月9日下午，在稻渔空间乡村生态观光园，习近平观看宁夏特色农产品展示，了解稻渔种养业融合发展的创新做法。他强调，发展现代特色农业和文化旅游业，必须贯彻以人民为中心的发展思想，突出农民主体地位，把保障农民利益放在第一位。\r\n\r\n这些年，贺兰县稻渔空间乡村生态观光园所在的四十里店村，正从一个“空心村”变成一个“网红村”。通过利用流转土地发展制种产业，搞现代化新型农业建设，推动多产业融合，让农民在“家门口”脱贫致富。这一变化折射出宁夏践行乡村振兴战略的积极探索。\r\n\r\n6月9日 习近平在贺兰山东麓葡萄种植园考察调研 来源：新华社\r\n\r\n当天，习近平还来到位于银川市西夏区的贺兰山东麓葡萄种植园考察。习近平同正在作业的工人和技术人员交流，了解葡萄种植技术、产品销路和收入情况。他还为葡萄酒产业发展指出方向——增加文化内涵，加强宣传推介，打造自己的知名品牌，提高附加值和综合效益。\r\n\r\n关注地区特色产业，推动产业向高端化、绿色化、智能化、融合化方向发展。在此前赴陕西、山西考察时，习近平也都重点关注了当地的木耳、黄花菜等特色产业，尤其关心群众在其中的收益情况。在陕西，他指出，发展扶贫产业，重在群众受益，难在持续稳定。在山西，他强调，要把群众受益摆在突出位置。要因地制宜发展乡村产业，精心选择产业项目，确保成功率和可持续发展。\r\n\r\n“‘输血’更‘造血’，通过产业扶贫建立更加有效、更加长效的利益联结机制，确保乡亲们持续获益。可以说，产业扶贫是稳定脱贫的长久之策。”雷明表示，“产业扶贫既是打赢脱贫攻坚战的抓手，也是实现乡村振兴有效衔接的着力点。发展特色产业，重在提升产业的扶贫效能，一是要建产兴业，二是更加有效地瞄准市场瞄准需求。”\r\n\r\n“加快转变经济发展方式，加快产业转型升级，加快新旧动能转换……”谈及不懈推动宁夏高质量发展时，习近平还提出了三个“加快”。对此，中国农业大学国家乡村振兴研究院副院长、人文与发展学院左停表示，“当前我们面临百年未有之大变局，发展方式、增长方式都需要适应新的时代挑战，‘三个加快’就是要求我们不断地推进改革、不断地推进创新。全面建成小康社会不是终点，而是新的起点。着眼于第二个百年的奋斗目标，乡村建设需要深化改革、加快创新。”\r\n\r\n“国以民为本，社稷亦为民而立”。此次宁夏之行，习近平处处挂念着人民群众的福祉，正如他两会期间参加内蒙古代表团审议时所强调的，党团结带领人民进行革命、建设、改革，根本目的就是为了让人民过上好日子，无论面临多大挑战和压力，无论付出多大牺牲和代价，这一点都始终不渝、毫不动摇。 ', '2020-06-11 05:52:16', 'wam', '无', '无', 0);
INSERT INTO `news` VALUES (18, '时政新闻', '总书记刚刚来过这里丨鱼肥水美宁夏川 稻花香里说丰年', '人民网-宁夏频道', ' 稻香鱼肥，水美人勤。\r\n\r\n6月9日下午，习近平总书记考察调研了银川市贺兰县稻渔空间乡村生态观光园。', ' 在园区展厅，宁夏农业农村厅厅长王刚向总书记汇报了宁夏现代农业的发展情况。“2016年7月习近平总书记在宁夏视察时，提出要构建现代农业三大体系，调整优化农业产业结构，把现代特色农业做实做强。总书记的重要指示，为宁夏农业的发展指明了方向，提供了重要遵循，也使宁夏的农业迎来了发展的春天。”王刚说。\r\n\r\n2016年以来，宁夏坚决贯彻落实总书记的重要指示要求，聚焦优质粮食、现代畜牧、瓜菜、枸杞、酿酒葡萄五个特色优势产业。推进调优种养结构、调大经营规模、调强加工能力、调长产业链条。实现了农业发展的布局区域化、经营规模化、生产标准化、发展产业化。现在，全区现代特色农业产值占农业总产值的比重达到了87.4%。\r\n\r\n“我们十几个人分两组正在为稻田画补插秧苗，总书记沿着田埂走到我们身边停下脚步，亲切地向大家打招呼。”回想起和总书记对话的情形，农业技术员吕永杰依旧心绪难平。\r\n\r\n看到总书记来了，大家纷纷停下手里的活围拢过来。\r\n\r\n“总书记像邻家大叔，非常和蔼，问我们生活怎么样？还问了一些农业方面的问题。能够向总书记介绍、解答，我感到特别自豪。”2014年从宁夏大学农学院毕业的吕永杰，在稻渔空间工作了快6年，她见证了稻渔空间所在地的四十里店村从传统农业耕种到发展现代化新型农业的转变。\r\n\r\n“我们在自己的土地上，亲手建设了这么一个好地方，总书记能来看看是我最大的愿望。”村民陈学龙说，“我们打工一天能挣120元，一个月收入3600元，每年还有土地流转费等多项收入。日子有奔头，干活很带劲！”\r\n\r\n“现在收入高了，生活好了，农民的日子今非昔比。”家住附近四十里店村的60岁村民陈红军见到了总书记，心里一直十分激动，“大伙儿都好想把总书记请到屋里坐坐哩！”据陈红军介绍，家里的25亩土地在公司入了股，每亩地每年保底收入800元，年底分红50元，去年光土地流转收入就超过了2万元。\r\n\r\n曾当选“全国十佳农民”的负责人赵建文向总书记介绍了稻渔空间的建设发展情况和稻渔种养业融合发展的创新做法。“我们通过‘公司+合作社+基地+农户+服务’的生产经营模式，走产业化经营道路，构筑包括水稻种植、加工生产、电子商务等环节在内的全产业链条，让农业生产插上绿色高效的翅膀。”赵建文表示。\r\n\r\n几年前，四十里店村一直沿袭着传统农耕农作模式，农业基础配套设施十分薄弱，全村整体收入长期处于贺兰县中下水平。现在，利用流转土地发展制种产业，搞现代化新型农业建设，农业生产、科学管理、农艺展示、农产品加工等项目实现有机结合。从种植、养殖、生产、流通、电商，到休闲农业、旅游、娱乐等等，一二三产业在这里深度融合发展。\r\n\r\n“总书记在园区里看得非常仔细，听得非常认真，不时和大家交流，勉励我们要百尺竿头、更进一步。”贺兰县县委书记赵波说，总书记还把鱼苗补到了池塘里，就是为我们洒下了希望的种子。\r\n\r\n长廊下游客们正在品尝瓜果、水田间三五一组的农民忙着劳作、别具风格的玻璃观光栈桥伸向稻田腹地、登上木质观景塔巨大的稻田画尽收眼底……一幅生动秀美的“塞上江南、鱼米之乡”画卷在蓝天白云下铺展。', '2020-06-11 05:52:53', 'wam', '无', '无', 0);
INSERT INTO `news` VALUES (19, '时政新闻', '习近平讲故事：“小黄花大产业”', '人民网－人民日报海外版', '11日下午，习近平总书记抵达山西考察调研，首站来到大同。他强调，乡亲们脱贫后，我最关心的是如何巩固脱贫、防止返贫，确保乡亲们持续增收致富。一下车，总书记就直奔田间，深入了解当地黄花产业发展情况。', ' 黄花又名萱草、忘忧草，既能食用，也能药用。大同黄花种植有600多年历史，近年来大力发展标准化、规模化种植，种植面积达到26万亩，年产值达9亿元，带动了1.5万多户贫困户脱贫致富。正如古话所讲：“莫道农家无宝玉，遍地黄花是金针。”\r\n\r\n村民们正在地里除草、松土，看到总书记来了，兴奋地围拢过来，高声向总书记问好。\r\n\r\n习近平同大家攀谈起来：土地是不是流转了？一亩地能给多少钱？加上做工，一年下来能赚多少钱？\r\n\r\n大家告诉总书记，土地流转后每亩地一年收入500元，在这里做工一天还能赚150元。这些年，在龙头企业、合作社引领下，黄花产量品质稳定，销路和价格也有保障，去年带动贫困户户均收入1万多元。\r\n\r\n“原来不敢多种，现在基地办了加工厂，烘干、加工、储藏、销售都不愁了，黄花越种越多，收入也越来越多。”村民们兴奋地说。\r\n\r\n听了大家的“致富经”，习近平十分高兴：“我上个月去了陕西秦岭山区的一个村叫金米村，那里种木耳形成了产业化，我称赞他们是‘小木耳大产业’。你们这里也是‘小黄花大产业’，很有发展前途。”\r\n\r\n他叮嘱当地干部，一定要保护好、发展好黄花这个产业，让它成为乡亲们致富的一个好门路，变成群众的“致富花”。“共产党人就要为民办事、为民造福，要扎扎实实为老百姓办实事办好事，为官一任、造福一方。”\r\n\r\n在大同，黄花相关产品已经形成系列。黄花产品展厅里，黄花干、黄花酱、黄花饼、黄花制作的化妆品等各色产品琳琅满目。通过深加工，延长产业链，提升综合效益，当地老百姓走出了一条幸福路。展厅墙上贴着“忘忧草，脱贫宝”标语，生动诠释了黄花种植给乡亲们带来的好处。\r\n\r\n习近平细致察看黄花产品，感慨地说：“就是要立足本地实际，大力发展特色产业，把大同黄花做成全国知名品牌，让乡亲们富而忘忧。”\r\n\r\n——摘自《蹚出新路子 书写新篇章——习近平总书记山西考察纪实》（人民日报 2020年5月14日1版）', '2020-06-11 05:53:25', 'wam', '无', '无', 0);
INSERT INTO `news` VALUES (20, '科技新闻', '5G手机国内出货占比近半 “杀手级”应用还有多远', '李娜', '记者梳理发现，从目前手机终端厂商的布局来看，影音娱乐、影像创作、云游戏成为最主要的三个应用场景。', '5G手机的普及时代正在来临。\r\n\r\n　　6月10日，中国信通院发布2020年5月国内手机市场运行分析报告。数据显示，2020年5月，国内市场5G手机出货量1564.3万部，占同期手机出货量的46.3%；上市新机型16款，占同期手机上市新机型数量的50.0%。1~5月，国内市场5G手机累计出货量4608.4万部、上市新机型累计81款，占比分别为37.0%和47.9%。\r\n\r\n　　从数据来看，5G手机的出货量正在稳步增长，并且正在逐步逼近4G手机的出货量。在调研机构Counterpoint的全年预测中，今年仅仅是中国市场的5G手机数量就将突破100款，全年出货量预计将超过1.5亿部。\r\n\r\n　　而随着5G手机价格的持续下探，5G的全面普及似乎就在眼前，但对于5G杀手级的应用到底是什么？\r\n\r\n　　以2G为例，当时正值模拟电路向数字电路的转换期，手机开始变得越来越轻巧，短信开始流行。而3G的出现，则将手机与互联网连接起来，带来了GPS、游戏、流媒体音乐服务和社会化媒体，比如QQ等。\r\n\r\n　　4G更是掀起了一场新的智能手机革命，微信、抖音、移动支付等各类APP出现，带来了大量全新的、令人兴奋的应用，社交、共享单车。通过4G，还可以与语音助手交流，在智能手机和始终连接的PC上同时进行作业。\r\n\r\n　　记者梳理发现，从目前手机终端厂商的布局来看，影音娱乐、影像创作、云游戏成为最主要的三个应用场景。\r\n\r\n　　“5G网络催生了大视频、高清视频的需求，同时，高清、大视频以及相关应用的传输、存储、交互又对网络通信提出了更高的要求和挑战。”中兴通讯副总裁、终端产品经营中心主任罗炜对第一财经记者表示，中兴定义当下为“全民视频”时代，目前聚焦于“通信技术+视频影像”赛道。\r\n\r\n　　可以看到，从快手到抖音，从朋友圈小视频到B站，再到vlog，以及现在火爆的直播卖货，5G正在激发全民影像创作，并且颠覆了以往的视频形式，实现了低门槛、云共享、全景拍。\r\n\r\n　　无论是看视频还是拍视频，用户对于手机在影像处理上的要求变得越来越高。除了中兴外，从去年年底开始，包括OPPO、vivo以及小米在内的手机厂商都在影像处理尤其是在视频处理上做更大的投入。\r\n\r\n　　国元证券发布报告预测，2020年销售手机所搭载的单机摄像头的平均数量将增加25.7%，所搭载的摄像头像素增速则为40.3%，若仅考虑安卓手机，数量和像素增速则更高，分别达到30.1%和45.7%。\r\n\r\n　　此外，IoT（物联网）行业也正在成为包括手机厂商布局的战略重点，尤其是千元5G手机时代的到来有望加速IoT行业的整体爆发。\r\n\r\n　　荣耀总裁赵明对记者表示，手机和智慧屏、平板、笔记本电脑都不是孤立的产品，各个终端的内容都可以互通。目前IoT行业发展还停留在线状阶段，手机可以遥控IoT，但不同IoT产品之间的互联不足，成熟的生态应该是网状的。\r\n\r\n　　华为集团内部已将“1+8+N”全场景战略定义为“长期战略”。其中，1是作为主入口的手机，8是PC、平板、智慧屏、音箱、眼镜、手表、耳机、车机等八种入口级设备。而OPPO也表示已不再是单纯的手机公司，未来将继续围绕“个人、家庭、办公、出行”四大场景，布局核心入口，稳步丰富IoT产品布局。\r\n\r\n　　此外，vivo在5G场景上的设想是以智能手机为主，以AR眼镜、智能手表和智能耳机为辅，形成多元业务矩阵。以智慧识物为例，AI助手可以通过手机摄像头自动识别待添加设备并引导用户完成添加与连接。\r\n\r\n　　“人工智能，5G连接和硬件、软件设备在技术和工程上飞速发展，正在以前所未有的速度创造一个融合性的未来世界。在这个未来中，重要的不是具体产品的形态沿革，而是基于服务和需求，基于技术发展所不断拓宽的可能性，将用户接入到科技所能创造的全连接、全场景、全交互的智慧服务生态体系世界之中。”vivo执行副总裁胡柏山认为。\r\n\r\n　　罗炜则表示，智能终端将向智慧终端演进，具备超强的感知能力，带来全新的连接，凭借端计算、边缘计算和云计算三重计算能力，一个5G+IoT+AI+融合的智慧时代全面来临。', '2020-06-11 05:55:02', 'wam', '无', '无', 0);
INSERT INTO `news` VALUES (21, '科技新闻', '命途多舛的iPhone 12能够顺利发售吗？', ' 新浪科技综合  ', '在安卓手机不断堆料的情况下，苹果按部就班的产品迭代多少显得不够看。5G商用已经将近一年，安卓厂商将5G手机的价格一路推到2000元，但苹果依旧没有推出第一款5G手机。这让不少“观望”的消费者对iPhone 12给予厚望。', '然而，iPhone 12也可能是近几年来最命途多舛的苹果手机，在2020年初新冠肺炎疫情的冲击下，苹果的供应链一度陷入停产的状态。虽然随着伴随着疫情的好转，富士康等苹果代工厂逐渐恢复运转，但iPhone 12的生产进度缓慢，甚至可能会延迟发售。\r\n\r\n　　DigiTimes前段时间发布消息称，6.1英寸的iPhone 12 Max和iPhone 12 Pro将在7-8月开始量产，而另外两款机型的量产时间会有所推迟。这一消息虽然没有得到证实，但苹果供应商博通公司的CEO Hock Tan在上周四举办的财报电话会议上表示，一家“大型北美手机”客户的重要产品将会延迟发布。 \r\n\r\n　　虽然没有明确指出该品牌，但基本上可以确定为苹果。据了解博通是苹果主要的硬件供应商之一，主要为iPhone提供WiFi、蓝牙芯片等硬件。博通在北美除了苹果之外，没有其他大型手机公司客户。\r\n\r\n　　Hock Tan还称，由于产品延误，公司无线业务收入将在今年第三季度出现下滑，第四季度才会有所好转。这意味着，iPhone 12将在今年第四季度发布。据了解，除了iPhone X和iPhone XR等少数型号延迟上市外，苹果手机一般在9月发布与上市。\r\n\r\n　　虽然此前有传言称今年苹果将发布4款旗舰iPhone，但此消息依然存在不确定性。业内人士认为，iPhone 12的延迟可能来自两方面的原因，一方面今年iPhone有可能将会进行大升级以补全短板，另一方面疫情的影响可能导致生产线压力加大，可能产值产量跟不上。有传言称今年苹果可能会发布两款普通款iPhone 12，而iPhone 12 Pro可能会推迟到2021年。\r\n\r\n　　虽然距离iPhone 12的发布依然还有段较长的时间，但iPhone 12的相关传言已经层出不穷。有传言称6.7英寸的iPhone和6.1英寸的机型将使配备三镜头相机的高端OLED设备，而5.4和6.1英寸的机型将使具有双镜头相机且价格更加实惠的入门级iPhone。\r\n\r\n　　在设计方面，苹果分析师郭明錤表示新款iPhone将使用更复杂的分段设计，并采用新的挖沟和注塑工艺，以此减少技术屏蔽层对内部天线高频传输效率的负面影响，整体设计将类似于iPhone 4。\r\n\r\n　　值得注意的是，4款iPhone 12都将采用OLED 屏幕，其中iPhone 2 Pro系列将采用120Hz ProMotion显示屏，主要类似于iPad Pro上的120Hz刷新原理，会根据屏幕上内容的移动情况动态调整显示屏。\r\n\r\n　　外媒报道称，今年可能是iPhone 大年。随着被压抑的用户设备升级需求的释放，iPhone 12 系列新品或在苹果公司历史上留下重要的一笔。即便新机的配置和功能不会有大的变动，但恰到好处的升级，仍有助于激发新老用户的购买欲。Webbush 分析师在本周披露的一份说明中指出：“Apple Watch 的持续成功、AirPods 销售的稳步增长、以及 iPhone 12 系列新品的发布，有望在今夏为苹果股票投资者带来极大的惊喜”。\r\n\r\n　　分析师 Daniel Ives 补充道，目前约有 3.5 亿 iPhone 用户处于被压抑的升级窗口，涵盖不同价位和尺寸的 iPhone 12 全系阵容可在 9 月更好地满足用户的实际需求。\r\n\r\n　　虽然对于苹果而言，推迟iPhone 12 Pro系列的发售时间，可能使其错过一轮换机高峰期，但贸然采取四款手机同步发售，依然有可能导致其产能不足，进而影响品牌口碑。正如前文所说，iPhone 12对于苹果而言是一款别具意义的产品，因此围绕这一款产品制定的每一个策略都将以稳妥为主。\r\n\r\n　　不过一直以来苹果都缺席手机行业的春季竞赛，iPhone 12 Pro系列的延迟发售可能为手机行业带来一次激烈的竞争。苹果可能会面临华为、三星等品牌的围剿，甚至可能会对苹果日后的市场策略产生深远的影响。', '2020-06-11 05:56:08', 'wam', '无', '无', 0);
INSERT INTO `news` VALUES (22, '科技新闻', 'NASA：SpaceX载人龙飞船返回时间为7月末或8月初', '李明', '北京时间6月11日下午消息，据国外媒体报道，美国宇航局（NASA）官员日前表示，他们考虑让刚刚进入国际空间站的两名美国宇航员于7月末或8月初返回地球。', '北京时间5月31日凌晨，一枚SpaceX“猎鹰9号”火箭在NASA肯尼迪航天中心39A发射台发射升空，将载有两名美国宇航员鲍勃·贝肯（Bob Behnken）和道格·赫利（Doug Hurley）的SpaceX“龙飞船”太空舱送入轨道，并成功与国际空间站（ISS）对接。无论是对于NASA，还是SpaceX，这都是一次意义非凡的发射。\r\n\r\n　　据NASA预计，贝肯和赫利将在国际空间站停留1~4个月。具体的停留时间取决于多种因素，包括太阳能电池板的退化、下一次发射使用的“龙飞船”的状况，以及着陆区的天气情况等。而NASA官员日前表示，他们考虑让两名宇航员于7月末或8月初返回地球。\r\n\r\n　　NASA负责载人航天项目的代理局长肯·鲍尔索克斯（Ken Bowersox）昨日表示：“我们没有规定上一次任务的时间长度，直至我们将机组人员送入轨道，然后看看载人‘龙飞船’的表现。目前来看，飞船表现很不错。我们认为，机组人员在那里停留一两个月是合理的。具体细节仍在制定中。”\r\n\r\n　　如果一切顺利，NASA预计SpaceX“龙飞船”的下一次任务“Crew-1”在今年8月底发射或9月初发射。届时，Crew-1将运载三名NASA宇航员，分别为维克多·格洛弗（Victor Glover）、迈克·霍普金斯（Mike Hopkins）和山农·沃克（Shannon Walker）；以及一名日本宇航员Soichi Noguchi。\r\n\r\n　　为了执行Crew-1任务，贝肯与赫利必须在计划发射日期的前几周返回地球，以便对载人“龙飞船”进行评估和认证。为此，NASA正考虑让两名宇航员在7月末或8月初返回地球。', '2020-06-11 05:56:59', 'wam', '无', '无', 0);
INSERT INTO `news` VALUES (23, '法治新闻', '广州破获特大网络赌博案 “直播带赌”涉案逾3亿元', '程景伟', '广州警方10日通报，该市荔湾区警方近日在“飓风2020”专项打击行动中，破获一起利用网络直播带人赌博的特大网络赌博案，涉及赌博资金流水逾3亿元，抓获孔某(男，29岁，东莞市人)、李某(男，24岁，广州市人)等涉赌嫌疑人32名', '该网络赌博团伙成员均为20岁出头的年轻男女，有的还是在校大学生。在招揽赌博对象时，该团伙主要针对珠三角地区喜欢球类运动的富庶男青年，“主播”通过宣称有VIP美女服务等色诱手段，吸引男性年轻球迷参与赌博。“美女客服”提出球迷只要充值到一定数额，可以私下见面交友。\r\n\r\n　　球迷赌客充值成功后，可在赌博APP上进行博彩，赌博VIP群内有“主管”“主播”“客服”等人员24小时不间断向赌客提供博彩信息，赌客可在群内随时联系充值参赌。赌博方式也是五花八门，包括足球、篮球、排球、电子竞技等，且每种类型赌博方式不尽相同，以足球为例，比赛来自世界各地，包括地区赛事、国际赛事甚至虚拟赛事等。如因为疫情停赛，赌博团伙会寻找其他电子竞技类比赛，如英雄联盟、王者荣耀等比赛。\r\n\r\n　　为了增加赌博活跃度，赌博团伙还引入营销策略，设置“充值返还”“充值抽奖”等活动，如充值5000元返还588元，以抢红包形式抽奖品牌手机等。\r\n\r\n　　今年5月13日，广州荔湾警方多个抓捕小组分别在广州、东莞、佛山、深圳等地将17名主要团伙成员和15名参赌人员抓获归案。经警方调查，该赌博团伙今年以来通过网络直播引诱赌博赌资流水达3亿多元。据团伙成员交代，团伙中普通成员月收入均在万元以上。\r\n\r\n　　目前，经警方调查取证，其中孔某等15名主要涉案嫌疑人已被依法刑事拘留，其他涉赌人员被依法行政拘留，案件仍在进一步侦办中。(完)', '2020-06-11 05:57:55', 'wam', '无', '无', 0);
INSERT INTO `news` VALUES (24, '法治新闻', '山东省高院：涉未成年被害人案件农村儿童占近七成', '邢婷', '　　山东省高院近日以山东省近两年千余件侵害未成年人权益的案件为样本，对侵害农村未成年人案件的情况和特点进行了详细分析。该研究显示，涉及未成年被害人的所有案件中，农村未成年被害人平均年龄9.7岁，其中超过一半不满10周岁；就个案而言，年龄在13岁的农村未成年被害人居多。', '随着法治乡村工作的不断推进，近两年，针对农村未成年人实施的严重暴力犯罪有明显下降趋势，但一些农村黑恶势力逐渐抬头，并刻意拉拢招募当地未成年人，教唆、引诱其实施犯罪，组织胁迫未成年少女卖淫，严重损害了未成年人的身心健康，受到法律严惩。\r\n\r\n　　在严重暴力犯罪中，农村未成年人常成为案件双方当事人矛盾激化后的无辜受害者。上述分析显示，因农村邻里纠纷被杀害的未成年人占19.1%；因父母婚恋纠纷（家庭琐事、离婚、一方出轨等）迁怒孩子致死的案件占42.4%，甚至出生几个月的婴儿也未能幸免。\r\n\r\n　　此外，针对农村未成年人实施的性侵犯罪尤为突出。随着农村大批人员外出务工，留守儿童作为在农村生活的主要群体之一，因缺少父母必要的监护和照管，成为一些犯罪分子的性侵对象。上述样本分析显示，在农村未成年被害人案件中，67.7%的案件系性侵未成年人犯罪，且大多是熟人作案，有79.1%的未成年被害人与犯罪人相识，23.5%的案件发生在邻里之间。\r\n\r\n　　尽管拐卖、拐骗儿童案件占比逐年降低，但网络犯罪的特征日益明显，一些犯罪分子在网站上隐匿发布“送养”“领养”等信息，为非法收养儿童提供帮助，非法牟利。上述样本分析显示，山东各级法院审理的拐卖儿童案件中，利用网络实施犯罪的比例从2016年的6.8%上升到2019年的19.8%。\r\n\r\n　　为更好地呵护农村未成年人健康成长，山东省高院创造性开展护航“三农”工作，充分发挥少年审判职能，严厉打击侵害农村未成年人的暴力犯罪和黑恶势力犯罪，该从重的依法从重，该判处死刑的坚决处以死刑。其中判处五年以上刑罚的罪犯为41.3%。缓刑适用率为5.4%，比全省刑事案件缓刑适用率低17个百分点。\r\n\r\n　　同时，加大对农村未成年被害人的司法援助力度。2019年，山东各级法院对30名农村未成年人进行司法援助共计价值126万元。', '2020-06-11 05:58:34', 'wam', '无', '无', 0);
INSERT INTO `news` VALUES (25, '国内新闻', '北京丰台新增2例新冠肺炎确诊病例', '新京报', '北京两天新增3例新冠肺炎确诊病例。昨日（12日），丰台区通报新增2名确诊病例。两人同为中国肉类食品综合研究中心员工，其中一人曾经与西城区确诊病例产生交集，两人均前往过新发地菜市场。', '两人为同一单位员工，其中一人曾赴青岛出差；丰台、西城确诊病例曾在新发地菜市场有“交集”\r\n\r\n北京两天新增3例新冠肺炎确诊病例。昨日（12日），丰台区通报新增2名确诊病例。两人同为中国肉类食品综合研究中心员工，其中一人曾经与西城区确诊病例产生交集，两人均前往过新发地菜市场。截至发稿时，与西城区确诊病例密切接触者的核酸检测均呈阴性。北京市教委也发布消息称，暂停小学低年级学生返校安排。\r\n\r\n继6月11日西城区通报一例新冠肺炎确诊病例后，6月12日下午，北京市召开第113场疫情防控新闻发布会。丰台区副区长张婕介绍，12日通报两例新增病例均来自丰台区，为同一单位员工，丰台区将加大社区和单位管控力度，对病例所在小区单位封闭式管理。\r\n\r\n北京市政府新闻发言人徐和建介绍，疫情风险随时随地存在，常态化防控容不得半点松懈。只要一天没有取得疫情防控的最终胜利，就必须时刻保持战时状态，坚决消除疫情反弹的可能。\r\n\r\n丰台两名确诊病例为同一单位员工\r\n\r\n张婕介绍了丰台区确诊2例新冠肺炎病例的有关情况。\r\n\r\n病例1，刘某某，男，北京人，25岁，中国肉类食品综合研究中心员工，家住丰台区西罗园街道马家堡东路，于6月9日独自骑车到辖区医院发热门诊就诊。患者曾出现咽痛、发热、咳嗽、流涕症状，最高体温38.6℃。患者自述近2周有出京史，到青岛出差5天，无境外人员、湖北接触史。就诊过程中全程佩戴口罩，6月12日，患者样本核酸检测为阳性，经专家会诊诊断为新冠肺炎确诊病例。\r\n\r\n病例2，尹某某，男，北京人，37岁，中国肉类食品综合研究中心员工，为病例1刘某某的同事，家住丰台区西罗园街道角门北路，于6月9日出现咽痒、有痰、轻微咳嗽，体温正常，未就诊和服药。6月12日，经区级排查，将尹某某用120急救车转运到辖区定点医院隔离排查，样本核酸检测为阳性，经专家诊断为新冠肺炎确诊病例。患者自述近2周无出京史，无境外人员、湖北接触史。\r\n\r\n病例所在小区和单位进行封闭式管理\r\n\r\n张婕介绍，目前，两例病例均在定点医院隔离治疗，现丰台区正在对患者进一步开展流行病学调查，对患者去过的场所进行人员和环境的采样和检测，追根溯源，做到“边流调、边检测、边防控”，实现应检尽检，全面摸排，落实病家和社区消毒，对密接人员落实集中隔离管理，切断传播途径。同时，加大社区和单位管控力度，对其所在小区和单位进行封闭式管理。\r\n\r\n下一步，丰台区将继续做好流行病学调查与社区管控等工作，加强对重点场所和重点人员的健康监测和环境采样；发挥医院发热门诊、社区卫生服务中心的哨点监测作用，摸排周边重点人员就诊的情况，做好预警监测。有后续情况将及时向社会公布。\r\n\r\n昨日，北京市新冠肺炎疫情防控工作新闻发布会通报称，已针对2例病例开展溯源，追踪密切接触者，开展环境采样及消毒等控制措施，截至15时，共追踪到密切接触者43名，均已转运至隔离点进行集中医学观察，并采集咽拭子标本进行核酸检测。此外，将在全市医疗机构发热门诊、急诊加强病例排查搜索，加强核酸检测和接触史问询。\r\n\r\n■ 回应\r\n\r\n青岛卫健委：排除病例在青岛感染可能\r\n\r\n针对北京通报丰台区两名新冠肺炎确诊病例，其中一人曾经前往青岛出差。昨日晚间，青岛卫健委通过官网发布北京市丰台区新冠肺炎确诊病例刘某某在青岛活动情况通报。\r\n\r\n通报称，病例刘某某自述近2周曾到青岛出差5天。获知信息后，我市疾控中心即与北京市疾控部门取得联系，初步了解，刘某某为北京某检测机构采样工作人员，到青岛出差从事本单位受托开展的市场肉类采样工作。根据了解到的信息，刘某某与北京市西城区6月11日确诊病例唐某某在北京新发地菜市场有过交集，唐某某6月3日曾到该市场采购肉类产品，刘某某6月5日曾到该市场采样。刘某某5月29日来青，6月2日返京，截至12日已离青10天，我市疾控部门专家研判排除其在青岛感染可能，按照流行病学调查规范，我市不需要追查密切接触者。\r\n\r\n■ 关注\r\n\r\n西城确诊病例活动轨迹公布\r\n\r\n昨日，市疾控中心副主任庞星火介绍了西城区确诊病例的情况。\r\n\r\n西城确诊病例曾去新发地买菜\r\n\r\n据介绍，西城区确诊病例家住西城区月坛街道西便门东大街，自述近2周无出京史、无外来人员密切接触史，通过进一步流行病学调查显示，5月31日前往丰台区京荟广场蹦床区、地下室及乐图空间、新中客餐厅（大成路店）。6月1日自驾车前往西山森林公园游玩，后到西城区槐柏树街北里5号楼底商新发地便民菜店买菜。\r\n\r\n3日前往丰台区京开五金大楼二层购物，新发地菜市场地下层采购海鲜和肉，前往中国联通（长椿街营业厅），到首航超市（真武庙店）购物，4日至5日自述未外出，6日出现间断性发热，畏寒，乏力等症状，但无咳嗽、无咽痛、无胸闷，自服头孢及中药，7日至8日自述未外出，9日到中通快递（西客站分部）邮寄物品，后到西便门西里11号楼北一栋芳邻便民菜站买菜。\r\n\r\n10日下午到宣武医院发热门诊就诊，11日患者样本核酸检测为阳性，结合肺部影像、血液检查结果，经专家会诊诊断为新冠肺炎确诊病例。\r\n\r\n西城密接者38人核酸标本均为阴性\r\n\r\n截至发稿时，确定西城管理的密切接触者38人，已经集中隔离23人，1人居家隔离（1岁婴儿），14人正在安排集中隔离转运。38人中已采核酸标本21人，检测结果均为阴性。\r\n\r\n西城区对确诊病例的孩子及其所在班级其余33名学生，和该学生有过接触的15名教职员工进行了核酸检测，结果均为阴性。昨天上午，已对同一楼层的其他班级学生和教职员工进行核酸采样，结果待报。居住小区两栋居民楼共417人，已采集咽拭子33人，血清328人，检测结果均为阴性。居民楼其余人员的样本结果待报。\r\n\r\n此外，采集患者家环境标本29件，冰箱及食品涂抹标本11件，居住小区公共区域外环境标本23件，采集患者孩子所在学校外环境标本20件，采集患者在西城活动场所外环境标本36件，进行核酸检测，结果均为阴性。\r\n\r\n确诊者孩子所在班级暂时停课\r\n\r\n西城区确诊病例子女为育民小学四年级学生。昨日，记者探访发现，学校教学活动仍正常进行。\r\n\r\n昨日上午，刚把孩子送进学校的一位家长表示，班级群已发出通知称，四年级一位同学是密切接触者。“这个班暂时停课了，其他年级学生可以自愿请假。”', '2020-06-13 09:08:01', 'lxy', '无', '无', 1);
INSERT INTO `news` VALUES (26, '国内新闻', '31省区市新增11例新冠肺炎，北京新增6例本土病例', '刘光博 ', '6月12日0—24时，31个省（自治区、直辖市）和新疆生产建设兵团报告新增确诊病例11例，其中境外输入病例5例（天津1例，上海1例，广东1例，海南1例，四川1例），本土病例6例，均在北京；无新增死亡病例；无新增疑似病例。', '当日新增治愈出院病例2例，解除医学观察的密切接触者635人，无重症病例。\r\n\r\n境外输入现有确诊病例66例（无重症病例），现有疑似病例1例。累计确诊病例1808例，累计治愈出院病例1742例，无死亡病例。\r\n\r\n截至6月12日24时，据31个省（自治区、直辖市）和新疆生产建设兵团报告，现有确诊病例74例（无重症病例），累计治愈出院病例78367例，累计死亡病例4634例，累计报告确诊病例83075例，现有疑似病例1例。累计追踪到密切接触者751383人，尚在医学观察的密切接触者3197人。\r\n\r\n31个省（自治区、直辖市）和新疆生产建设兵团报告新增无症状感染者7例（境外输入5例）；当日无转为确诊病例；当日解除医学观察13例（境外输入2例）；尚在医学观察无症状感染者98例（境外输入48例）。\r\n\r\n累计收到港澳台地区通报确诊病例1596例。其中，香港特别行政区1108例（出院1060例，死亡4例），澳门特别行政区45例（出院45例），台湾地区443例（出院431例，死亡7例）。', '2020-06-13 10:07:49', 'lxy', '无', '无', 2);
INSERT INTO `news` VALUES (27, '国内新闻', '北京突发新增7例本土病例 李兰娟：一定要强化食品溯源', '健康时报 记者王艾冰', '“目前来看，尽快清晰的知道哪些食品是被污染的，以及这些食品的最初来源在哪里非常重要，这对新冠病毒的溯源也具有一定的参考价值。另外，要对近一个月出入市场的所有人员进行检测，这也是非常有必要的。”中国工程院院士、国家卫生健康委高级别专家组成员李兰娟在谈及北京疫情时告诉健康时报记者。', '6月11日，北京新增1例本土新冠肺炎确诊病例，通过随后公布的活动轨迹表明，该患者曾到新发地菜市场地下层采购海鲜和肉；6月13日上午，北京市新型冠状病毒疫情防控工作新闻发布会通报，6月12日0时-24时，北京市新增6例病例，且均有新发地农产品批发市场活动史。至此，北京市公布的最近突增7例确诊病例均与新发地农产品批发市场相关联。\r\n\r\n“批发市场人流量非常大，市场上的东西和人员都来自四面八方，比较容易带来和传播病毒。”李兰娟院士提醒，“因为现在我们还不知道到底是市场里的食品是传染源，还是经过市场的人是传染源，所以要对市场里的人和食品全部进行溯源，对于人的溯源我们已经做的很好了，所以对于物品的溯源我们需要强化一下。”\r\n\r\n李兰娟介绍，“因为我们现在警惕性都很高了，针对北京的情况，只要把所有的感染源都找到了，不要有遗漏就不会大爆发。”关键还在于对感染的源头要抓住，然后把去过新发地农产品批发市场的人都找到，并且进行检测，也就不会引起大的流行。”\r\n\r\n除此之外，针对北京市目前情况，李兰娟院士表示，“原来我们已经做的措施都要继续，不要麻痹大意。北京市民必须按照原来的要求，严格佩戴口罩、接触了外来的东西要及时洗手消毒。夏天到了，一定要注意卫生、还应该加强粪便管理。”', '2020-06-13 10:00:34', 'wam', '无', '无', 4);
INSERT INTO `news` VALUES (32, '国内新闻', '北京公布36例新增确诊病例详情，均与新发地市场有关', '人民日报客户端 ', '6月14日中午，北京市新型冠状病毒肺炎疫情防控工作第115场新闻发布会召开。北京市疾控中心副主任庞星火介绍了6月13日0时至24时北京新增新冠肺炎确诊病例的有关情况。', '庞星火说，6月13日0时至24时，北京市新增新冠肺炎确诊病例36例，阳性检测者1例。确诊病例中男性15例，女性21例；北京户籍8例，其他省户籍28例；现住丰台区30例，大兴区4例，房山区1例，西城区1例；在新发地市场工作者27例，曾直接或间接暴露于新发地市场者9例；通过主动就医发现11例，疫情溯源筛查主动发现25例。\r\n\r\n　　病例1，某男，35岁，河北保定人，工作单位为北京新发地市场，个体经营者，现住新发地经营者乐园。8日出现发热、干咳、流涕、乏力、头晕和肌肉酸痛等症状，最高体温38℃。12日就诊天坛医院，核酸检测阳性，经专家会诊为确诊病例，临床分型为轻型。\r\n\r\n　　病例2，某女，34岁，重庆人，工作单位为李记川菜，现住丰台区花乡首经贸育菲园东里1号院。其工作单位的食材供货商为北京新发地批发市场、岳各庄市场及大洋路批发市场。患者8日出现乏力、全身酸痛等症状，10日至12日先后就诊北京华坛中西医结合医院和北京天坛医院，核酸检测阳性，经专家会诊为确诊病例，临床分型为普通型。\r\n\r\n　　病例3，某女，58岁，北京市丰台区人，退休，现住丰台区太平桥街道万泉寺南里。患者几乎每天下午在新发地菜篮子超市买菜，7日出现发冷、鼻塞、流涕等症状，最高体温37.8℃。11日就诊宣武医院，核酸检测阳性，经专家会诊为确诊病例，临床分型为轻型。\r\n\r\n　　病例4，某男，56岁，北京市西城区人，某公司总经理，现住大兴区高米店街道西斯莱公馆。4日曾前往新发地市场采购水产品和水果。9日出现肌肉酸痛、发热等症状，最高体温39.4℃。10日就诊大兴区人民医院，核酸检测阳性，经专家会诊为确诊病例，临床分型为普通型。\r\n\r\n　　病例5，某女，病例4之妻，51岁，北京市西城区人，离退人员，现住大兴区高米店街道西斯莱公馆。4日曾前往新发地市场采购水产品和水果。7日出现头晕、乏力、咳痰、咽痛等症状，最高体温38.6℃。12日被转至大兴区人民医院隔离治疗，核酸检测阳性，经专家会诊为确诊病例，临床分型为普通型。\r\n\r\n　　病例6，某女，46岁，浙江丽水人，工作单位为新发地市场，个体经营者，现住新发地经营者乐园。10日下午出现头晕、恶心、发热，最高体温38℃，12日就诊天坛医院，核酸检测阳性，经专家会诊为确诊病例，临床分型为普通型。\r\n\r\n　　病例7，某男，32岁，安徽宿州人，工作单位为新发地市场，个体经营者，现住丰台区银地家园。自述近14天无任何症状，12日在疫情溯源采样检测中发现核酸检测阳性，经专家会诊为确诊病例，临床分型为普通型。\r\n\r\n　　病例8，某女，28岁，河北保定人，工作单位为新发地市场，个体经营者，现住新发地经营者乐园。自述近14天无任何症状，12日在疫情溯源采样检测中发现核酸检测阳性，经专家会诊为确诊病例，临床分型为普通型。\r\n\r\n　　病例9，某女，43岁，吉林人，工作单位为新发地市场，个体经营者，现住花乡乡宜兰园。9日出现肌肉酸痛、发冷、寒战、腹泻等症状，12日在疫情溯源采样检测中发现核酸检测阳性，经专家会诊为确诊病例，临床分型为普通型。\r\n\r\n　　病例10，某男，为病例9之夫，44岁，吉林人，工作单位为新发地市场，个体经营者，现住花乡乡宜兰园。10日出现咽痛、肌肉酸痛、咳嗽等症状，12日在疫情溯源采样检测中发现核酸检测阳性，经专家会诊为确诊病例，临床分型为普通型。\r\n\r\n　　病例11，某男，41岁，北京大兴人，工作单位为某医药药材公司职工，现住大兴区林校路街道车站中里。5月28日至11日6次在新发地市场牛羊肉大厅采购。11日出现发热、头痛等症状，最高体温37.8℃，就诊大兴区人民医院，核酸检测阳性，经专家会诊为确诊病例，临床分型为轻型。\r\n\r\n　　病例12，某男，43岁，河北保定人，工作单位为新发地市场，个体经营者，现住北京丰台花乡街道天骄俊园。4日出现头疼、全身乏力症状，未就诊。12日在疫情溯源采样检测中发现核酸检测阳性，经专家会诊为确诊病例，临床分型为普通型。\r\n\r\n　　病例13，某女，38岁，河南信阳人，工作单位为新发地市场，个体经营者，现住花乡乡天伦锦城。自述近14天无任何不适症状，12日在疫情溯源采样检测过程中检出核酸阳性，经专家会诊为确诊病例，临床分型为轻型。\r\n\r\n　　病例14，某女，42岁，河北省保定人，工作单位为新发地市场，个体经营者，现住新发地经营者乐园。自述近14天无任何不适症状。12日在疫情溯源采样检测过程中检出核酸阳性，经专家会诊为确诊病例，临床分型为普通型。\r\n\r\n　　病例15，某男，32岁，河北雄安人，工作单位为新发地市场，个体经营者，现住新发地经营者乐园。11日出现咽痛，自测体温37℃。12日在疫情溯源采样检测过程中检出核酸阳性，经专家会诊为确诊病例，临床分型为轻型。\r\n\r\n　　病例16，某男，54岁，河北保定人，工作单位为新发地市场，个体经营者，现住新发地经营者乐园。8日出现乏力等症状，最高体温38℃。12日在疫情溯源采样检测过程中检出核酸阳性，经专家会诊为确诊病例，临床分型为普通型。\r\n\r\n　　病例17，某男，56岁，北京西城人，工作单位为北京民航机场巴士公司，现住西城区永宁胡同。3日到新发地市场采购，5日出现乏力、头痛，12日出现发热，最高体温38℃。12日先后就诊于宣武医院、人民医院和海淀医院，核酸检测阳性，经专家会诊为确诊病例，临床分型为普通型。\r\n\r\n　　病例18，某男，27岁，河北省保定人，工作单位为北京新发地市场，个体经营者，现住新发地经营者乐园。11日出现咳嗽、咳痰、乏力等症状，无发热、咽痛等其他不适症状，未就医。12日在疫情溯源采样检测中，核酸检测阳性，经专家会诊为确诊病例，临床分型为轻型。\r\n\r\n　　病例19，某男，38岁，河北保定人，工作单位为北京新发地市场，个体经营者，现住新发地经营者乐园。10日自觉感冒、咽痒、打喷嚏等症状，未就医。12日在疫情溯源采样检测中，核酸检测阳性，经专家会诊为确诊病例，临床分型为轻型。\r\n\r\n　　病例20，某男，28岁，河北保定人，工作单位为北京新发地市场，个体经营者，现住新发地经营者乐园。10日自觉发热，未就医。12日在疫情溯源采样检测中，核酸检测阳性，经专家会诊为确诊病例，临床分型为普通型。\r\n\r\n　　病例21，某女，27岁，江西人，工作单位为北京新发地市场，个体经营者，现住北京市丰台区天伦锦城。4日出现流涕、打喷嚏、乏力、肌肉酸痛等症状，稍事休息后好转，未就诊。12日在疫情溯源采样检测中，核酸检测阳性，经专家会诊为确诊病例，临床分型为普通型。\r\n\r\n　　病例22，某女，35岁，湖北钟祥人，工作单位为北京新发地市场，个体经营者，现住北京市丰台区新发地天骄俊园9号院。11日出现头晕、乏力等症状。12日在疫情溯源采样检测中，核酸检测阳性，经专家会诊为确诊病例，临床分型为普通型。\r\n\r\n　　病例23，某女，49岁，江苏南通人，工作单位为北京新发地市场，个体经营者，现住北京市丰台区新发地天骄俊园9号院。自述近14天无任何不适症状。12日在疫情溯源采样检测中，核酸检测阳性，经专家会诊为确诊病例，临床分型为轻型。\r\n\r\n　　病例24，某女，46岁，北京海淀人，工作单位中建资产管理有限公司，财务部经理，现住北京市大兴区西红门镇瑞海家园。6日曾到新发地市场采购，11日出现发热伴肌肉酸痛，体温最高37.8℃。12日先后前往大兴区人民医院和天坛医院就诊，核酸检测阳性，经专家会诊为确诊病例，临床分型为普通型。\r\n\r\n　　病例25，某女，31岁，河北保定人，工作单位为玉泉东市场海鲜市场，个体经营者，现住北京市丰台区青塔秀园。8日出现发热、乏力、打喷嚏、流涕、腹泻症状，最高体温38.5℃。其妻子有多次新发地海鲜市场暴露史，但其本人无新发地市场暴露史。12日就诊武警总医院，核酸检测阳性，经专家会诊为确诊病例，临床分型为普通型。\r\n\r\n　　病例26，某女，61岁，黑龙江哈尔滨人，工作单位为北京新发地市场，负责保洁，现住丰台区花乡陈留村。自述10日出现咽干、发热等症状，未就医。12日在疫情溯源采样检测中，核酸检测阳性，经专家会诊为确诊病例，临床分型为普通型。\r\n\r\n　　病例27，某女，46岁，河南周口人，工作单位为北京新发地市场，个体经营者，现住北京市丰台区潘家庙400号。自述近期除失眠外无任何不适症状，未就诊。12日在疫情溯源采样检测中，核酸检测阳性，经专家会诊为确诊病例，临床分型为无症状感染者。\r\n\r\n　　病例28，某女，50岁，河北保定人，工作单位为新发地市场，个体经营者，现住新发地经营者乐园。10日出现咽干症状，未就医。12日在疫情溯源采样检测中，核酸检测阳性，经专家会诊为确诊病例，临床分型为轻型。\r\n\r\n　　病例29，某女，33岁，河北保定人，工作单位为新发地市场，个体经营者，现住丰台区宜兰园一区。自述近14天无任何不适症状。12日在疫情溯源采样检测中，核酸检测阳性，经专家会诊为确诊病例，临床分型为轻型。\r\n\r\n　　病例30，某男，29岁，河北保定人，工作单位为北京新发地市场，个体经营者，现住丰台区新发地天骄骏园8号院。自述近14天无任何不适症状，12日在疫情溯源采样检测中，核酸检测阳性，经专家会诊为确诊病例，临床分型为轻型。\r\n\r\n　　病例31，某男，25岁，河南周口人，工作单位为北京新发地市场，个体经营者，现住北京市丰台区天伦锦城。自述近14天无任何不适症状，12日在疫情溯源采样检测中，核酸检测阳性，经专家会诊为确诊病例，临床分型为轻型。\r\n\r\n　　病例32，某女，34岁，福建人，工作单位为北京新发地市场，个体经营者，现住北京市新发地经营者乐园。自述近14天无任何不适症状，12日在疫情溯源采样检测中，核酸检测阳性，经专家会诊为确诊病例，临床分型为轻型。\r\n\r\n　　病例33，某女，50岁，河北保定人，工作单位为北京新发地市场，个体经营者，现住北京市丰台区新发地天伦锦城。自述近14天无任何不适症状，12日在疫情溯源采样检测中，核酸检测阳性，经专家会诊为确诊病例，临床分型为轻型。\r\n\r\n　　病例34，某男，38岁，安徽巢湖人，工作单位为新发地市场，个体经营者，现住北京市丰台区宜兰园。自述近14天无任何不适症状，12日在疫情溯源采样检测中，核酸检测阳性，经专家会诊为确诊病例，临床分型为轻型。\r\n\r\n　　病例35，某女，35岁，北京丰台人，工作单位为新发地市场，个体经营者，现住新发地经营者乐园。12日在疫情溯源采样检测中，核酸检测阳性，经专家会诊为确诊病例，临床分型为轻型。\r\n\r\n　　病例36，某女，37岁，北京市西城区人，无业，现住北京市房山区长阳镇九州溪雅苑。5日曾到新发地市场采购，自述无发热、咳嗽、腹泻等症状。11日陪同丈夫于石景山医院发热门诊就诊，12日核酸检测阳性，经专家会诊为确诊病例，临床分型为无症状感染者。', '2020-06-14 04:24:12', 'wam', '无', '无', 11);
INSERT INTO `news` VALUES (33, '国际新闻', '德国电信内部文件：若德政府不用华为5G技术，将至少损失30亿欧元', '青木', '据英国路透社报道，德国电信6月19日举行年度股东大会，该公司首席执行官霍特格斯在回答有关中国华为技术的问题时表示，反对禁止采用华为公司的电信网络设备，“排除一个提供商会降低我们的灵活性，我们反对一揽子禁令。”', '德国《商报》近日报道称，德国电信的一份内部文件评估认为，如果德国联邦政府反对使用华为的5G技术，该公司预计将至少损失30亿欧元；而淘汰已经安装在4G网络关键部位的华为产品，可能需要长达5年的时间。\r\n\r\n德国电信6月17日开始向用户提供5G服务，预计到7月中旬，德国一半的人口将能够使用5G。', '2020-06-20 04:24:12', 'cz', '无', '无', 4);

-- ----------------------------
-- Table structure for topic
-- ----------------------------
DROP TABLE IF EXISTS `topic`;
CREATE TABLE `topic`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主题id',
  `topicname` varchar(8) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '主题名',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `topicname`(`topicname`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 11 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

-- ----------------------------
-- Records of topic
-- ----------------------------
INSERT INTO `topic` VALUES (5, '军事新闻');
